import React, { useRef, useState } from 'react';
import { Canvas, useFrame, useThree } from '@react-three/fiber';
import { OrbitControls, Grid, Box } from '@react-three/drei';
import * as THREE from 'three';

// 3D家庭成员组件
const FamilyMember3D = ({ member, onUpdate, isSelected, onSelect }) => {
  const meshRef = useRef();
  const [isDragging, setIsDragging] = useState(false);
  const [dragStart, setDragStart] = useState(null);

  const size = member.size || 1;
  const color = member.gender === 'male' ? '#3b82f6' : '#ec4899';

  // 处理拖拽开始
  const handlePointerDown = (e) => {
    e.stopPropagation();
    setIsDragging(true);
    setDragStart({
      x: e.point.x,
      z: e.point.z,
      memberX: member.x,
      memberZ: member.z
    });
    onSelect && onSelect(member.id);
  };

  // 处理拖拽移动
  const handlePointerMove = (e) => {
    if (!isDragging || !dragStart) return;

    const deltaX = e.point.x - dragStart.x;
    const deltaZ = e.point.z - dragStart.z;

    const newX = dragStart.memberX + deltaX;
    const newZ = dragStart.memberZ + deltaZ;

    // 限制在棋盘范围内
    const clampedX = Math.max(-10, Math.min(10, newX));
    const clampedZ = Math.max(-10, Math.min(10, newZ));

    onUpdate && onUpdate(member.id, { x: clampedX, z: clampedZ });
  };

  // 处理拖拽结束
  const handlePointerUp = () => {
    setIsDragging(false);
    setDragStart(null);
  };

  // 处理大小调整
  const handleWheel = (e) => {
    e.stopPropagation();
    const delta = e.deltaY > 0 ? -0.1 : 0.1;
    const newSize = Math.max(0.5, Math.min(3, size + delta));
    onUpdate && onUpdate(member.id, { size: newSize });
  };

  // 处理旋转
  const handleDoubleClick = (e) => {
    e.stopPropagation();
    const currentRotation = member.rotation || 0;
    const newRotation = (currentRotation + Math.PI / 2) % (Math.PI * 2);
    onUpdate && onUpdate(member.id, { rotation: newRotation });
  };

  return (
    <group
      ref={meshRef}
      position={[member.x, size * 0.5, member.z]}
      rotation={[0, member.rotation || 0, 0]}
      onPointerDown={handlePointerDown}
      onPointerMove={handlePointerMove}
      onPointerUp={handlePointerUp}
      onWheel={handleWheel}
      onDoubleClick={handleDoubleClick}
    >
      {/* 球体头部 */}
      <mesh position={[0, size * 0.6, 0]}>
        <sphereGeometry args={[size * 0.3, 16, 16]} />
        <meshStandardMaterial color={color} />
      </mesh>

      {/* 圆锥体身体 */}
      <mesh position={[0, size * 0.2, 0]}>
        <coneGeometry args={[size * 0.4, size * 0.8, 8]} />
        <meshStandardMaterial color={color} />
      </mesh>

      {/* 选中状态指示器 */}
      {isSelected && (
        <mesh position={[0, -0.1, 0]}>
          <ringGeometry args={[size * 0.5, size * 0.6, 16]} />
          <meshBasicMaterial color="#6366f1" />
        </mesh>
      )}

      {/* 死亡标记 */}
      {member.isDeceased && (
        <mesh position={[0, size * 0.8, 0]}>
          <boxGeometry args={[size * 0.1, size * 0.1, size * 0.1]} />
          <meshBasicMaterial color="#6b7280" />
        </mesh>
      )}
    </group>
  );
};

// 3D画布组件
const Canvas3D = ({ members, onUpdateMember, selectedMember, onSelectMember }) => {
  return (
    <div style={{ width: '100%', height: '600px' }}>
      <Canvas
        camera={{ position: [0, 15, 15], fov: 50 }}
        shadows
      >
        {/* 环境光 */}
        <ambientLight intensity={0.4} />

        {/* 方向光 */}
        <directionalLight
          position={[10, 10, 5]}
          intensity={1}
          castShadow
          shadow-mapSize-width={2048}
          shadow-mapSize-height={2048}
        />

        {/* 控制器 */}
        <OrbitControls
          enablePan={true}
          enableZoom={true}
          enableRotate={true}
          maxPolarAngle={Math.PI / 2}
          minDistance={5}
          maxDistance={30}
        />

        {/* 棋盘网格 */}
        <Grid
          args={[20, 20]}
          cellSize={1}
          cellThickness={0.5}
          cellColor="#6b7280"
          sectionSize={5}
          sectionThickness={1}
          sectionColor="#374151"
          fadeDistance={30}
          fadeStrength={1}
          followCamera={false}
          infiniteGrid={true}
        />

        {/* 家庭成员 */}
        {members.map(member => (
          <FamilyMember3D
            key={member.id}
            member={member}
            onUpdate={onUpdateMember}
            isSelected={selectedMember === member.id}
            onSelect={onSelectMember}
          />
        ))}

        {/* 地面 */}
        <mesh rotation={[-Math.PI / 2, 0, 0]} position={[0, -0.1, 0]} receiveShadow>
          <planeGeometry args={[25, 25]} />
          <meshStandardMaterial color="#f3f4f6" />
        </mesh>
      </Canvas>
    </div>
  );
};

export default Canvas3D;
