import React, { forwardRef, useEffect } from 'react';
import { useDrop } from 'react-dnd';
import FamilyMember from './FamilyMember';
import './index.css';

const MIN_DISTANCE = 0;

function getNonOverlappingPosition(x, y, members, currentId, memberSize, canvasWidth, canvasHeight) {
  let safeX = x;
  let safeY = y;
  let tries = 0;

  console.log('üîç ÈáçÂè†Ê£ÄÊµãÂºÄÂßã:', { 
    ËæìÂÖ•‰ΩçÁΩÆ: { x, y }, 
    ÊàêÂëòÂ∞∫ÂØ∏: memberSize, 
    ÁîªÂ∏ÉÂ∞∫ÂØ∏: { canvasWidth, canvasHeight },
    ÂÖ∂‰ªñÊàêÂëòÊï∞Èáè: members.filter(m => m.id !== currentId).length
  });

  while (tries < 30) {
    const overlappingMembers = members.filter(
      m =>
        m.id !== currentId &&
        Math.abs(m.x - safeX) < memberSize - MIN_DISTANCE &&
        Math.abs(m.y - safeY) < memberSize - MIN_DISTANCE
    );
    
    const isOverlapping = overlappingMembers.length > 0;

    if (!isOverlapping) {
      if (tries > 0) {
        console.log('‚úÖ ÊâæÂà∞Êó†ÈáçÂè†‰ΩçÁΩÆ:', { 
          ÊúÄÁªà‰ΩçÁΩÆ: { x: safeX, y: safeY }, 
          Â∞ùËØïÊ¨°Êï∞: tries 
        });
      }
      break;
    }

    console.log(`‚ö†Ô∏è Á¨¨${tries + 1}Ê¨°ÈáçÂè†Ê£ÄÊµã:`, { 
      ÂΩìÂâç‰ΩçÁΩÆ: { x: safeX, y: safeY }, 
      ÈáçÂè†ÊàêÂëò: overlappingMembers.map(m => ({ name: m.name, pos: { x: m.x, y: m.y } }))
    });

    safeX = Math.max(0, Math.min(safeX + 15, canvasWidth - memberSize));
    safeY = Math.max(0, Math.min(safeY + 15, canvasHeight - memberSize));
    tries++;
  }

  if (tries >= 30) {
    console.log('‚ö†Ô∏è ÈáçÂè†Ê£ÄÊµãËææÂà∞ÊúÄÂ§ßÂ∞ùËØïÊ¨°Êï∞');
  }

  return { x: safeX, y: safeY };
}

const CANVAS_WIDTH = 1200;
const CANVAS_HEIGHT = 500;

const Canvas = forwardRef(({ members, updateMemberPosition, onUpdateMember }, ref) => {
  // ÊâìÂç∞ÁîªÂ∏ÉÂ∞∫ÂØ∏‰ø°ÊÅØ
  console.log('üé® Canvas Ê∏≤Êüì - Â∞∫ÂØ∏:', { width: CANVAS_WIDTH, height: CANVAS_HEIGHT });
  
  // ÁõëÂê¨ÁîªÂ∏ÉDOMÂèòÂåñ
  useEffect(() => {
    const canvas = document.getElementById('canvas-area');
    if (canvas) {
      const rect = canvas.getBoundingClientRect();
      const computedStyle = window.getComputedStyle(canvas);
      const devicePixelRatio = window.devicePixelRatio || 1;
      
      console.log('üé® Canvas DOM ËØ¶ÁªÜ‰ø°ÊÅØ:', {
        ËÆæËÆ°Â∞∫ÂØ∏: { width: CANVAS_WIDTH, height: CANVAS_HEIGHT },
        ÂÆûÈôÖDOMÂ∞∫ÂØ∏: { width: rect.width, height: rect.height },
        DOM‰ΩçÁΩÆ: { left: rect.left, top: rect.top },
        CSSÊ†∑ÂºèÂ∞∫ÂØ∏: { 
          width: computedStyle.width, 
          height: computedStyle.height 
        },
        Áº©ÊîæÊØî‰æã: { 
