          y: rect.height / CANVAS_HEIGHT 
        },
        ËÆæÂ§áÂÉèÁ¥†ÊØî: devicePixelRatio,
        ËßÜÂè£‰ø°ÊÅØ: {
          innerWidth: window.innerWidth,
          innerHeight: window.innerHeight,
          scrollX: window.scrollX,
          scrollY: window.scrollY
        }
      });
    }
  }, [members.length]); // ÂΩìÊàêÂëòÊï∞ÈáèÂèòÂåñÊó∂ÈáçÊñ∞Ê£ÄÊü•

  const [, drop] = useDrop({
    accept: 'MEMBER',
    drop: (item, monitor) => {
      console.log('üöÄ ÂºÄÂßãÊãñÊãΩÊîæÁΩÆÊìç‰Ωú - ÊàêÂëòID:', item.id);
      
      const clientOffset = monitor.getClientOffset();
      const canvas = document.getElementById('canvas-area');
      if (!clientOffset || !canvas) {
        console.log('‚ùå Êó†Ê≥ïËé∑ÂèñÂÆ¢Êà∑Á´ØÂÅèÁßªÈáèÊàñÁîªÂ∏ÉÂÖÉÁ¥†');
        return;
      }
      
      const canvasRect = canvas.getBoundingClientRect();
      console.log('üìê ÁîªÂ∏É‰ΩçÁΩÆ‰ø°ÊÅØ:', {
        canvasRect: {
          left: canvasRect.left,
          top: canvasRect.top,
          width: canvasRect.width,
          height: canvasRect.height
        },
        clientOffset: {
          x: clientOffset.x,
          y: clientOffset.y
        }
      });
      
      // ÊâæÂà∞ÂΩìÂâçÊãñÊãΩÁöÑÊàêÂëò
      const member = members.find(m => m.id === item.id);
      if (!member) {
        console.log('‚ùå Êâæ‰∏çÂà∞ÂØπÂ∫îÁöÑÊàêÂëò');
        return;
      }
      
      console.log('üë§ ÊãñÊãΩÂâçÊàêÂëò‰ø°ÊÅØ:', {
        id: member.id,
        name: member.name,
        currentPosition: { x: member.x, y: member.y },
        size: { width: member.width || 72, height: member.height || 72 },
        initialOffset: item.initialOffset
      });
      
      const memberWidth = member.width || 72;
      const memberHeight = member.height || 72;

      // ËÆ°ÁÆóÈº†Ê†áÂú®ÁîªÂ∏ÉÂÜÖÁöÑÁõ∏ÂØπ‰ΩçÁΩÆ
      const relativeX = clientOffset.x - canvasRect.left;
      const relativeY = clientOffset.y - canvasRect.top;
      
      console.log('üìç Èº†Ê†áÁõ∏ÂØπÁîªÂ∏É‰ΩçÁΩÆ:', { relativeX, relativeY });

      // ‰ΩøÁî®ÊãñÊãΩÂºÄÂßãÊó∂ËÆ∞ÂΩïÁöÑÂÅèÁßªÈáèÊù•ËÆ°ÁÆóÂáÜÁ°Æ‰ΩçÁΩÆ
      let x, y;
      if (item.initialOffset && item.initialOffset.x !== null && item.initialOffset.y !== null) {
        // ‰ΩøÁî®ÂàùÂßãÂÅèÁßªÈáèËÆ°ÁÆó‰ΩçÁΩÆ
        x = relativeX - item.initialOffset.x;
        y = relativeY - item.initialOffset.y;
        console.log('‚úÖ ‰ΩøÁî®ÂàùÂßãÂÅèÁßªÈáèËÆ°ÁÆó:', {
          initialOffset: item.initialOffset,
          calculatedPosition: { x, y }
        });
      } else {
        // ÈôçÁ∫ßÊñπÊ°àÔºöÂÅáËÆæ‰ªé‰∏≠ÂøÉÊãñÊãΩ
        x = relativeX - memberWidth / 2;
        y = relativeY - memberHeight / 2;
        console.log('‚ö†Ô∏è ‰ΩøÁî®ÈôçÁ∫ßÊñπÊ°àÔºà‰∏≠ÂøÉÊãñÊãΩÔºâ:', {
          calculatedPosition: { x, y }
