import React, { useState, useEffect, useCallback } from 'react';
import { useDrag } from 'react-dnd';

const maleRoles = ['Áà∂‰∫≤', 'Á•ñÁà∂', 'Âì•Âì•', 'ÂºüÂºü', 'ÂÑøÂ≠ê', 'ÁªßÁà∂', 'ÂÖªÁà∂', '‰∏àÂ§´'];
const femaleRoles = ['ÊØç‰∫≤', 'Á•ñÊØç', 'ÂßêÂßê', 'Â¶πÂ¶π', 'Â•≥ÂÑø', 'ÁªßÊØç', 'ÂÖªÊØç', 'Â¶ªÂ≠ê'];
const childRoles = ['ÂÑøÂ≠ê', 'Â•≥ÂÑø', 'Â≠©Â≠ê'];
const directionOrder = ['up', 'right', 'down', 'left'];
const directionAngle = { up: 0, right: 90, down: 180, left: 270 };

const FamilyMember = ({ member, onUpdate }) => {
  const [{ isDragging }, drag] = useDrag({
    type: 'MEMBER',
    item: { id: member.id },
    collect: monitor => ({
      isDragging: monitor.isDragging(),
    }),
  });

  // Âà§Êñ≠ËæπÊ°ÜÂΩ¢Áä∂
  let borderRadius = 20; // ÈªòËÆ§ÂÄº
  if (member.gender === 'male') borderRadius = 8;
  else if (member.gender === 'female') borderRadius = 9999;
  else if (maleRoles.includes(member.role)) borderRadius = 8;
  else if (femaleRoles.includes(member.role)) borderRadius = 9999;

  // Á°Æ‰øùmemberÊúâwidthÂíåheightÂ±ûÊÄßÔºåÂê¶Âàô‰ΩøÁî®ÈªòËÆ§ÂÄº
  const width = member.width || (childRoles.includes(member.role) ? 48 : 72);
  const height = member.height || (childRoles.includes(member.role) ? 48 : 72);

  const [hovered, setHovered] = useState(false);
  const [resizing, setResizing] = useState(false);
  const [resizeStart, setResizeStart] = useState(null);

  const handleRotate = e => {
    e.stopPropagation(); // ÈòªÊ≠¢‰∫ã‰ª∂ÂÜíÊ≥°ÔºåÈÅøÂÖçËß¶ÂèëÊãñÊãΩ
    // direction ÈªòËÆ§ÂÄº‰∏∫ 'up'
    const current = member.direction || 'up';
    const idx = directionOrder.indexOf(current);
    const next = directionOrder[(idx + 1) % 4];
    onUpdate && onUpdate(member.id, { direction: next });
  };

  const handleResizeMouseDown = e => {
    e.stopPropagation(); // ÈòªÊ≠¢‰∫ã‰ª∂ÂÜíÊ≥°
    setResizing(true);
    setResizeStart({ x: e.clientX, y: e.clientY, width, height });
    document.body.style.setProperty('cursor', 'nwse-resize', 'important'); // ËÆæÁΩÆÊãñÊãΩÈº†Ê†áÊ†∑Âºè
  };

  useEffect(() => {
    if (!resizing) return;

    const handleMove = e => {
      const dx = e.clientX - resizeStart.x;
      const dy = e.clientY - resizeStart.y;
      // Á≠âÊØî‰æãÁº©Êîæ
      const delta = Math.max(Math.abs(dx), Math.abs(dy)) * (dx < 0 || dy < 0 ? -1 : 1);
      const base = Math.max(resizeStart.width, resizeStart.height);
      const newSize = Math.max(40, base + delta);

      // ‰øùÊåÅ‰∏≠ÂøÉÁÇπ‰∏çÂèò
      const centerX = member.x + resizeStart.width / 2;
      const centerY = member.y + resizeStart.height / 2;
      const newX = centerX - newSize / 2;
      const newY = centerY - newSize / 2;

      onUpdate && onUpdate(member.id, { width: newSize, height: newSize, x: newX, y: newY });
    };

    const handleUp = () => {
      setResizing(false);
      document.body.style.cursor = ''; // ÊÅ¢Â§çÈªòËÆ§Èº†Ê†áÊ†∑Âºè
    };

    window.addEventListener('mousemove', handleMove);
    window.addEventListener('mouseup', handleUp);

    return () => {
      window.removeEventListener('mousemove', handleMove);
      window.removeEventListener('mouseup', handleUp);
    };
  }, [resizing, resizeStart, onUpdate, member.id, member.x, member.y]); // Â¢ûÂä†‰æùËµñÈ°π

  const renderDirectionTriangle = useCallback(() => (
    <svg
      width="22"
      height="22"
      style={{
        position: 'absolute',
        top: -22, // ‰Ωç‰∫éÂç°ÁâáÈ°∂ÈÉ®‰∏äÊñπ
        left: '50%',
        zIndex: 20,
        transform: `translateX(-50%) rotate(${directionAngle[member.direction || 'up']}deg)` // Ê†πÊçÆÊúùÂêëÊóãËΩ¨
      }}
    >
      <polygon points="11,0 22,22 0,22" fill="#6366f1" />
    </svg>
  ), [member.direction]);

  const renderResizeHandle = useCallback(() => (
    <div
      onMouseDown={handleResizeMouseDown}
      style={{
        position: 'absolute',
        right: -8, // ÂÆö‰ΩçÂà∞Âè≥‰∏ãËßí
        bottom: -8, // ÂÆö‰ΩçÂà∞Âè≥‰∏ãËßí
        width: 18,
        height: 18,
        background: '#fff',
        border: '2px solid #6366f1',
        borderRadius: 6,
        cursor: 'nwse-resize', // Áº©ÊîæÊâãÊüÑÈº†Ê†áÊ†∑Âºè
        zIndex: 30,
        boxShadow: '0 1px 4px rgba(0,0,0,0.08)',
        display: hovered ? 'block' : 'none', // Èº†Ê†áÊÇ¨ÂÅúÊó∂ÊòæÁ§∫
      }}
      title="Áº©Êîæ"
    >
      <svg width="14" height="14" style={{ margin: 2 }}>
        <polyline points="2,12 12,12 12,2" fill="none" stroke="#6366f1" strokeWidth="2" />
      </svg>
    </div>
  ), [hovered, handleResizeMouseDown]);

  return (
    <div
      style={{ position: 'absolute', left: member.x, top: member.y, zIndex: 2000 }}
      onMouseEnter={() => setHovered(true)}
      onMouseLeave={() => setHovered(false)}
    >
      {/* ÊóãËΩ¨ÊåâÈíÆ */}
      <button
        onClick={handleRotate}
        style={{
          position: 'absolute',
          top: -30,
          right: 0,
          zIndex: 20,
          background: 'rgba(99,102,241,0.12)',
          border: 'none',
          borderRadius: '50%',
          width: 28,
          height: 28,
          cursor: 'pointer',
          display: hovered ? 'block' : 'none', // Èº†Ê†áÊÇ¨ÂÅúÊó∂ÊòæÁ§∫
          padding: 3,
          display: hovered ? 'flex' : 'none', // ‰ΩøÁî®flexÂ∏ÉÂ±ÄÂ±Ö‰∏≠ÂõæÊ†á
          alignItems: 'center',
          justifyContent: 'center'
        }}
        title="ÊóãËΩ¨ÊúùÂêë"
      >
        <svg width="16" height="16" viewBox="0 0 16 16">
          <path d="M8 2v2.5a.5.5 0 0 0 .5.5H11" stroke="#6366f1" strokeWidth="1.5" fill="none" />
          <path d="M13.5 8A5.5 5.5 0 1 1 8 2" stroke="#6366f1" strokeWidth="1.5" fill="none" />
        </svg>
      </button>
      {/* ÊàêÂëòÊ°ÜÊú¨‰Ωì */}
      <div
        ref={drag}
        style={{
          width,
          height,
          minWidth: 40,
          minHeight: 40,
          cursor: isDragging ? 'grabbing' : 'grab', // ÊãñÊãΩÊó∂ÊòæÁ§∫ÊäìÊâã
          opacity: isDragging ? 0.8 : 1, // ÊãñÊãΩÊó∂Á®çÂæÆÈÄèÊòé
          background: 'rgba(255,255,255,0.98)',
          border: '3px solid #a5b4fc',
          borderRadius,
          boxShadow: hovered ? '0 8px 24px rgba(0,0,0,0.2)' : '0 6px 20px rgba(0,0,0,0.15)',
          transition: 'box-shadow 0.2s ease, transform 0.2s ease',
          transform: isDragging ? 'scale(1.02)' : 'scale(1)', // ÊãñÊãΩÊó∂Á®çÂæÆÊîæÂ§ß
          willChange: 'transform',
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          justifyContent: 'center',
          userSelect: 'none',
          position: 'relative', // Á°Æ‰øùÂ≠êÂÖÉÁ¥†ÂÆö‰ΩçÊ≠£Â∏∏
        }}
      >
        {renderDirectionTriangle()}
        {/* Ê≠ª‰∫°ÂèâÂè∑ */}
        {member.isDeceased && (
          <div
            style={{
              position: 'absolute',
              top: '50%',
              left: '50%',
              transform: 'translate(-50%, -50%)',
              fontSize: Math.min(width * 2, 60),
              color: '#6b7280',
              fontWeight: 'bold',
              zIndex: 2001,
              textShadow: '2px 2px 4px rgba(0,0,0,0.3)',
              opacity: 0.6,
              pointerEvents: 'none',
            }}
          >
            ‚úï
          </div>
        )}
        <div
          style={{
            fontSize: Math.max(12, Math.min(width * 0.38, 28)),
            marginBottom: 2,
            transition: 'transform 0.2s',
          }}
        >
          {member.icon || 'üßë'}
        </div>
        <div
          style={{
            fontWeight: 'bold',
            color: '#1f2937',
            fontSize: Math.max(10, width / 6.5),
            marginBottom: 1,
          }}
        >
          {member.name}
        </div>
        <div
          style={{
            fontSize: Math.max(8, width / 8),
            color: '#3b82f6',
            fontWeight: 600,
            letterSpacing: 0.5,
          }}
        >
          {member.role}
        </div>
        {renderResizeHandle()}
      </div>
    </div>
  );
};

export default FamilyMember;
