import React, { useState, useEffect, useCallback } from 'react';
import { useDrag } from 'react-dnd';

const directionOrder = ['north', 'northeast', 'east', 'southeast', 'south', 'southwest', 'west', 'northwest'];
const directionAngle = { 
  north: 0, northeast: 45, east: 90, southeast: 135,
  south: 180, southwest: 225, west: 270, northwest: 315 
};

// 方形箭头位置
const arrowPositions = { /* 同原配置 */ };

// 三角形箭头位置（针对 polygon clipPath）
const triangleArrowPositions = { /* 同原配置 */ };

// 圆形箭头位置（新增，略微内收20px）
const circleArrowPositions = {
  north: { top: -20, left: '50%', transform: 'translateX(-50%)' },
  northeast: { top: 0, right: -20 },
  east: { top: '50%', right: -20, transform: 'translateY(-50%)' },
  southeast: { bottom: 0, right: -20 },
  south: { bottom: -20, left: '50%', transform: 'translateX(-50%)' },
  southwest: { bottom: 0, left: -20 },
  west: { top: '50%', left: -20, transform: 'translateY(-50%)' },
  northwest: { top: 0, left: -20 },
};

const FamilyMember = ({ member, onUpdate }) => {
  const [{ isDragging }, drag] = useDrag({
    type: 'MEMBER',
    item: { id: member.id },
    collect: monitor => ({ isDragging: monitor.isDragging() }),
  });

  const width = member.width || 72;
  const height = member.height || 72;
  const [hovered, setHovered] = useState(false);
  const [resizing, setResizing] = useState(false);
  const [resizeStart, setResizeStart] = useState(null);

  const validDirection = arrowPositions[member.direction] ? member.direction : 'north';
  const shape = member.gender === 'female' ? 'circle' : member.shape || 'square';

  const handleRotate = useCallback((e) => {
    e.preventDefault(); e.stopPropagation();
    const idx = directionOrder.indexOf(validDirection);
    const next = directionOrder[(idx + 1) % directionOrder.length];
    onUpdate?.(member.id, { direction: next });
  }, [validDirection, member.id, onUpdate]);

  const handleResizeMouseDown = useCallback((e) => {
    e.preventDefault(); e.stopPropagation();
    setResizing(true);
    setResizeStart({ x: e.clientX, y: e.clientY, width, height });
    document.body.style.setProperty('cursor', 'nwse-resize', 'important');
  }, [width, height]);

  useEffect(() => {
    if (!resizing || !resizeStart) return;
    const move = e => {
      const dx = e.clientX - resizeStart.x;
      const dy = e.clientY - resizeStart.y;
      const delta = Math.max(Math.abs(dx), Math.abs(dy)) * (dx + dy < 0 ? -1 : 1);
      const base = Math.max(resizeStart.width, resizeStart.height);
      const newSize = Math.max(40, base + delta);
      onUpdate?.(member.id, { width: newSize, height: newSize });
    };
    const up = () => {
      setResizing(false);
      setResizeStart(null);
      document.body.style.cursor = '';
    };
    document.addEventListener('mousemove', move);
    document.addEventListener('mouseup', up);
    return () => {
      document.removeEventListener('mousemove', move);
      document.removeEventListener('mouseup', up);
    };
  }, [resizing, resizeStart, onUpdate, member.id]);

  const renderDirectionTriangle = useCallback(() => {
    let positions = arrowPositions;
    if (shape === 'triangle') positions = triangleArrowPositions;
    else if (shape === 'circle') positions = circleArrowPositions;

    const pos = positions[validDirection];
    const angle = directionAngle[validDirection] ?? 0;
    const tf = `${pos.transform ?? ''} rotate(${angle}deg)`.trim();

    return (
      <svg width="22" height="22" style={{
        position: 'absolute',
        top: pos.top, left: pos.left,
        right: pos.right, bottom: pos.bottom,
        zIndex: 20, transform: tf
      }}>
        <polygon points="11,0 22,22 0,22" fill="#6366f1" />
      </svg>
    );
  }, [validDirection, shape]);

  const renderResizeHandle = useCallback(() => (
    <div onMouseDown={handleResizeMouseDown} style={{
      position: 'absolute', right: -25, bottom: -25,
      width: 24, height: 24, background: '#fff',
      border: '2px solid #6366f1', borderRadius: 8,
      cursor: 'nwse-resize', zIndex: 30,
      boxShadow: '0 2px 8px rgba(0,0,0,0.15)',
      display: 'flex', alignItems: 'center', justifyContent: 'center'
    }} title="缩放">
      <svg width="16" height="16">
        <polyline points="3,13 13,13 13,3" stroke="#6366f1" strokeWidth="2" fill="none"/>
        <polyline points="9,7 13,3 9,7" stroke="#6366f1" strokeWidth="2" fill="none"/>
        <polyline points="7,9 13,13 7,9" stroke="#6366f1" strokeWidth="2" fill="none"/>
      </svg>
    </div>
  ), [handleResizeMouseDown]);

  const getContainerStyle = () => {
    const base = {
      width, height, minWidth: 40, minHeight: 40,
      cursor: isDragging ? 'grabbing' : 'grab',
      opacity: isDragging ? 0.8 : 1,
      background: 'rgba(255,255,255,0.98)',
      border: '3px solid #a5b4fc',
      boxShadow: hovered ? '0 8px 24px rgba(0,0,0,0.2)' : '0 6px 20px rgba(0,0,0,0.15)',
      transition: 'box-shadow 0.2s ease, transform 0.2s ease',
      transform: isDragging ? 'scale(1.02)' : 'scale(1)',
      display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center',
      userSelect: 'none', position: 'relative',
    };
    if (shape === 'circle') base.borderRadius = '50%';
    else if (shape === 'triangle') {
      base.borderRadius = '0';
      base.clipPath = 'polygon(50% 0%, 0% 100%, 100% 100%)';
    } else {
      base.borderRadius = '8px';
    }
    return base;
  };

  return (
    <div
      style={{ position: 'absolute', left: member.x, top: member.y, zIndex: 2000 }}
      onMouseEnter={() => setHovered(true)}
      onMouseLeave={() => setHovered(false)}
    >
      <button onClick={handleRotate} onMouseDown={e => e.stopPropagation()} style={{
        position: 'absolute', top: -40, left: -40, zIndex: 20,
        background: '#fff', border: '2px solid #6366f1', borderRadius: '50%',
        width: 32, height: 32, cursor: 'pointer', padding: 0,
        display: 'flex', alignItems: 'center', justifyContent: 'center',
        boxShadow: '0 2px 8px rgba(0,0,0,0.15)'
      }} title={`箭头朝向: ${validDirection}`}>
        <svg width="18" height="18" viewBox="0 0 16 16">
          <path d="M8 2v2.5a.5.5 0 0 0 .5.5H11" stroke="#6366f1" strokeWidth="1.5" fill="none" />
          <path d="M13.5 8A5.5 5.5 0 1 1 8 2" stroke="#6366f1" strokeWidth="1.5" fill="none" />
        </svg>
      </button>

      <div ref={drag} style={getContainerStyle()}>
        {renderDirectionTriangle()}
        {member.isDeceased && (
          <div style={{
            position: 'absolute', top: '50%', left: '50%',
            transform: 'translate(-50%, -50%)',
            fontSize: Math.min(width * 2, 60),
            color: '#6b7280', fontWeight: 'bold',
            zIndex: 2001, textShadow: '2px 2px 4px rgba(0,0,0,0.3)',
            opacity: 0.6, pointerEvents: 'none'
          }}>✕</div>
        )}
        <div style={{
          fontSize: Math.max(12, Math.min(width * 0.38, 28)),
          marginBottom: 2, transition: 'transform 0.2s'
        }}>{member.icon || '🧑'}</div>
        <div style={{
          fontWeight: 'bold', color: '#1f2937',
          fontSize: Math.max(10, width / 6.5), marginBottom: 1
        }}>{member.name}</div>
        <div style={{
          fontSize: Math.max(8, width / 8),
          color: '#3b82f6', fontWeight: 600, letterSpacing: 0.5
        }}>{member.role}</div>
        {renderResizeHandle()}
      </div>
    </div>
  );
};

export default FamilyMember;
