import React, { useState, useEffect } from 'react';
// Assuming Canvas component is in a separate file and correctly imported
// import Canvas from './Canvas'; // Keep this line if Canvas is a separate file

// Placeholder for Canvas component if it's not provided, for standalone execution
// If you have a separate Canvas.js file, remove this placeholder.
const Canvas = ({ members, updateMemberPosition }) => {
  return (
    <div className="relative w-full h-96 bg-white border-4 border-blue-300 rounded-xl overflow-hidden shadow-inner flex items-center justify-center text-gray-400 text-3xl font-semibold">
      {members.length === 0 ? (
        <p>ËØ∑Ê∑ªÂä†ÂÆ∂Â∫≠ÊàêÂëò‰ª•ÂºÄÂßãÊéíÂàó</p>
      ) : (
        <div className="absolute inset-0">
          {members.map(member => (
            <div
              key={member.id}
              className="absolute bg-blue-200 text-blue-800 p-4 rounded-full shadow-lg cursor-grab active:cursor-grabbing transform -translate-x-1/2 -translate-y-1/2 flex flex-col items-center justify-center transition-all duration-100 ease-out"
              style={{ left: member.x, top: member.y, fontSize: '1.5rem', minWidth: '80px', minHeight: '80px' }}
              onMouseDown={(e) => {
                let startX = e.clientX;
                let startY = e.clientY;
                let initialX = member.x;
                let initialY = member.y;

                const onMouseMove = (moveEvent) => {
                  const newX = initialX + (moveEvent.clientX - startX);
                  const newY = initialY + (moveEvent.clientY - startY);
                  updateMemberPosition(member.id, newX, newY);
                };

                const onMouseUp = () => {
                  document.removeEventListener('mousemove', onMouseMove);
                  document.removeEventListener('mouseup', onMouseUp);
                };

                document.addEventListener('mousemove', onMouseMove);
                document.addEventListener('mouseup', onMouseUp);
              }}
              onTouchStart={(e) => {
                let startX = e.touches[0].clientX;
                let startY = e.touches[0].clientY;
                let initialX = member.x;
                let initialY = member.y;

                const onTouchMove = (moveEvent) => {
                  const newX = initialX + (moveEvent.touches[0].clientX - startX);
                  const newY = initialY + (moveEvent.touches[0].clientY - startY);
                  updateMemberPosition(member.id, newX, newY);
                };

                const onTouchEnd = () => {
                  document.removeEventListener('touchmove', onTouchMove);
                  document.removeEventListener('touchend', onTouchEnd);
                };

                document.addEventListener('touchmove', onTouchMove);
                document.addEventListener('touchend', onTouchEnd);
              }}
            >
              <span className="text-5xl mb-1">{member.icon}</span>
              <span className="font-bold text-xl">{member.name}</span>
              <span className="text-lg text-blue-700">{member.role}</span>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};


import axios from 'axios';

const PRESET_ROLES = [
  { role: 'Ëá™Â∑±', icon: 'üßë' },
  { role: 'Áà∂‰∫≤', icon: 'üë®' },
  { role: 'ÊØç‰∫≤', icon: 'üë©' },
  { role: 'Â≠©Â≠ê', icon: 'üßí' },
  { role: 'Á•ñÁà∂', icon: 'üë¥' },
  { role: 'Á•ñÊØç', icon: 'üëµ' },
  { role: 'ÈÖçÂÅ∂', icon: 'üíë' },
  { role: 'Âì•Âì•', icon: 'üßë‚Äçü¶±' },
  { role: 'ÂßêÂßê', icon: 'üë©‚Äçü¶∞' },
  { role: 'ÂºüÂºü', icon: 'üßë‚Äçüéì' },
  { role: 'Â¶πÂ¶π', icon: 'üëß' },
  { role: 'ÂÑøÂ≠ê', icon: 'üë¶' },
  { role: 'Â•≥ÂÑø', icon: 'üëß' },
  { role: 'ÁªßÁà∂', icon: 'üßî' },
  { role: 'ÁªßÊØç', icon: 'üë©‚Äçü¶≥' },
  { role: 'ÂÖªÁà∂', icon: 'üßì' },
  { role: 'ÂÖªÊØç', icon: 'üëµ' },
  { role: 'ÊúãÂèã', icon: 'ü§ù' },
  { role: 'ÂÆ†Áâ©', icon: 'üê∂' },
];

export default function App() {
  const [members, setMembers] = useState(() => {
    const saved = localStorage.getItem('members');
    return saved ? JSON.parse(saved) : [];
  });
  const [name, setName] = useState('');
  const [role, setRole] = useState('');
  const [icon, setIcon] = useState('üßë');
  const [analysis, setAnalysis] = useState('');
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    localStorage.setItem('members', JSON.stringify(members));
  }, [members]);

  const addMember = () => {
    if (!name || !role) return;
    // Set initial position for new members within the canvas boundaries
    const canvasWidth = 800; // Approximate canvas width, adjust if Canvas component has a fixed width
    const canvasHeight = 400; // Approximate canvas height
    const initialX = Math.random() * (canvasWidth - 100) + 50; // Random X within bounds
    const initialY = Math.random() * (canvasHeight - 100) + 50; // Random Y within bounds

    setMembers([...members, { id: Date.now(), name, role, icon, x: initialX, y: initialY }]);
    setName('');
    setRole('');
    setIcon('üßë');
  };

  const quickAdd = preset => {
    setRole(preset.role);
    setIcon(preset.icon);
    setName(''); // Clear name when quick adding a role
  };

  const updateMemberPosition = (id, x, y) => {
    setMembers(members.map(m => (m.id === id ? { ...m, x, y } : m)));
  };

  const generateDescription = () => {
    return members.map(m => `${m.name}Ôºà${m.role}Ôºâ‰Ωç‰∫é(${Math.round(m.x)}, ${Math.round(m.y)})`).join('Ôºå');
  };

  const analyze = async () => {
    setLoading(true);
