// App.js
import React, { useState, useEffect } from 'react';
import Canvas3D from './Canvas3D';
import axios from 'axios';

const PRESET_ROLES = [
  { role: '自己', icon: '🧑' }, { role: '父亲', icon: '👨' }, { role: '母亲', icon: '👩' },
  { role: '丈夫', icon: '👨' }, { role: '妻子', icon: '👩' }, { role: '儿子', icon: '👦' },
  { role: '女儿', icon: '👧' }, { role: '孩子', icon: '🧒' }, { role: '祖父', icon: '👴' },
  { role: '祖母', icon: '👵' }, { role: '配偶', icon: '💑' }, { role: '哥哥', icon: '🧑‍🦱' },
  { role: '姐姐', icon: '👩‍🦰' }, { role: '弟弟', icon: '🧑‍🎓' }, { role: '妹妹', icon: '👧' },
  { role: '继父', icon: '🧔' }, { role: '继母', icon: '👩‍🦳' }, { role: '养父', icon: '🧓' },
  { role: '养母', icon: '👵' }, { role: '朋友', icon: '🤝' }, { role: '宠物', icon: '🐶' },
];

export default function App() {
  const [members, setMembers] = useState(() => {
    const saved = localStorage.getItem('members');
    return saved ? JSON.parse(saved) : [];
  });
  const [name, setName] = useState('');
  const [role, setRole] = useState('');
  const [icon, setIcon] = useState('🧑');
  const [isDeceased, setIsDeceased] = useState(false);
  const [analysis, setAnalysis] = useState('');
  const [loading, setLoading] = useState(false);
  const [dragCompleted, setDragCompleted] = useState(false);
  const [selectedMember, setSelectedMember] = useState(null);
  const [gender, setGender] = useState('');

  useEffect(() => {
    localStorage.setItem('members', JSON.stringify(members));
  }, [members]);

  const addMember = () => {
    if (!name || !role || !gender) return;
    const x = (Math.random() - 0.5) * 16;
    const z = (Math.random() - 0.5) * 16;
    setMembers([
      ...members,
      { id: Date.now(), name, role, icon, isDeceased, gender, x, z, size: 1, rotation: 0 }
    ]);
    setName(''); setRole(''); setIcon('🧑'); setIsDeceased(false); setGender('');
  };

  const quickAdd = preset => {
    setRole(preset.role); setIcon(preset.icon); setName('');
  };

  const onUpdateMember = (id, changes) => {
    setMembers(members.map(m => m.id === id ? { ...m, ...changes } : m));
  };

  const generateDescription = () => {
    return members.map(m => {
      const status = m.isDeceased ? '（已故）' : '';
      return `${m.name}（${m.role}）${status}位于(${m.x.toFixed(1)}, ${m.z.toFixed(1)})，大小${m.size}`;
    }).join('，');
  };

  const analyze = async () => {
    setLoading(true); setAnalysis('');
    try {
      const res = await axios.post('http://127.0.0.1:5000/analyze', { description: generateDescription() });
      setAnalysis(res.data.analysis);
    } catch {
      setAnalysis('⚠️ 分析失败，请稍后重试。');
    }
    setLoading(false);
  };

  const clearMembers = () => {
    setMembers([]); setAnalysis(''); setDragCompleted(false); setSelectedMember(null);
  };

  const handleDragComplete = async () => {
    setDragCompleted(true); setLoading(true); setAnalysis('');
    try {
      const res = await axios.post('http://127.0.0.1:5000/analyze', { description: generateDescription() });
      setAnalysis(res.data.analysis);
    } catch {
      setAnalysis('⚠️ 分析失败，请稍后重试。');
    }
    setLoading(false);
  };

  return (
    <div className="min-h-screen bg-blue-50 py-6 px-4">
      <div className="max-w-7xl mx-auto bg-white rounded-3xl shadow-2xl p-6">
        <h1 className="text-4xl font-bold text-blue-700 text-center mb-6">3D家庭排列分析</h1>

        <div className="bg-blue-50 border border-blue-200 rounded-3xl shadow-lg p-8 mb-8">
          <div className="flex flex-wrap justify-center gap-3 mb-6">
            {PRESET_ROLES.map(preset => (
              <button key={preset.role} onClick={() => quickAdd(preset)}
                className="bg-blue-100 hover:bg-blue-300 text-blue-800 px-4 py-2 rounded-2xl text-xl font-bold flex items-center gap-1 border border-blue-300">
                <span className="text-2xl">{preset.icon}</span>{preset.role}
              </button>
            ))}
          </div>

          <div className="flex flex-wrap gap-4 justify-center mb-4">
            <input className="border p-4 text-xl rounded-xl w-60" placeholder="姓名" value={name} onChange={e => setName(e.target.value)} />
            <input className="border p-4 text-xl rounded-xl w-60" placeholder="关系" value={role} onChange={e => setRole(e.target.value)} />
            <select className="border p-4 text-xl rounded-xl" value={icon} onChange={e => setIcon(e.target.value)}>
              {PRESET_ROLES.map(p => <option key={p.role} value={p.icon}>{p.icon}</option>)}
            </select>
            <select className="border p-4 text-xl rounded-xl" value={gender} onChange={e => setGender(e.target.value)}>
              <option value="">选择性别</option>
              <option value="male">男</option>
              <option value="female">女</option>
            </select>
            <label className="flex items-center text-lg font-semibold">
              <input type="checkbox" checked={isDeceased} onChange={e => setIsDeceased(e.target.checked)} className="mr-2 w-5 h-5" /> 已故
            </label>
            <button onClick={addMember} className="bg-blue-600 hover:bg-blue-700 text-white px-6 py-3 rounded-xl text-xl font-bold">添加成员</button>
          </div>
        </div>

        <div className="bg-gray-900 rounded-3xl shadow-xl mb-8">
          <div className="p-4 bg-gray-800 text-white text-center">
            <h3 className="text-lg font-bold mb-2">3D家庭排列棋盘</h3>
            <p className="text-sm text-gray-300">拖拽移动 | 滚轮调整大小 | 双击旋转</p>
          </div>
          <Canvas3D
            members={members}
            onUpdateMember={onUpdateMember}
            selectedMember={selectedMember}
            onSelectMember={setSelectedMember}
          />
        </div>

        <div className="flex justify-center gap-4">
          <button onClick={handleDragComplete} disabled={loading || members.length === 0 || dragCompleted}
            className="px-8 py-3 rounded-xl font-bold bg-gradient-to-r from-purple-500 to-pink-500 text-white">
            {loading ? '分析中...' : dragCompleted ? '✅ 已完成' : '🧠 3D分析'}
          </button>
          <button onClick={analyze} disabled={loading || members.length === 0}
            className="px-8 py-3 rounded-xl font-bold bg-gradient-to-r from-green-400 to-blue-500 text-white">
            {loading ? '分析中...' : '🔍 文字分析'}
          </button>
          <button onClick={clearMembers} disabled={members.length === 0}
            className="px-8 py-3 rounded-xl font-bold bg-white text-gray-700 border border-gray-300">
            🧹 清空
          </button>
        </div>

        {analysis && (
          <div className="mt-10 p-6 bg-blue-50 border border-blue-200 rounded-2xl shadow-inner">
            <div className="text-lg font-bold text-blue-700 mb-3">🧠 分析结果：</div>
            <div className="text-gray-800 whitespace-pre-line">{analysis}</div>
          </div>
        )}
      </div>
    </div>
  );
}
