import React from 'react';
import { useDrop } from 'react-dnd';
import FamilyMember from './FamilyMember';
import './index.css';

const Canvas = ({ members, updateMemberPosition }) => {
  const [, drop] = useDrop(() => ({ accept: 'MEMBER' }));

  return (
    <div
      ref={drop}
      className="relative w-full h-96 bg-red-500 border-4 border-black rounded mb-4 overflow-hidden flex items-center justify-center"
      style={{ minHeight: 300 }}
    >
      {members.length === 0 ? (
        <div className="text-blue-400 text-lg select-none">请添加家庭成员并拖动到画布</div>
      ) : (
        members.map(member => (
          <FamilyMember
            key={member.id}
            member={member}
            updateMemberPosition={updateMemberPosition}
          />
        ))
      )}
    </div>
  );
};

export default Canvas;
