// canvas3d.js
import React, { useRef, useState } from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import { OrbitControls, Grid } from '@react-three/drei';
import * as THREE from 'three';

const FamilyMember3D = ({ member, onUpdate, isSelected, onSelect }) => {
  const meshRef = useRef();
  const [isDragging, setIsDragging] = useState(false);
  const [dragStart, setDragStart] = useState(null);
  const size = member.size || 1;
  const color = member.gender === 'male' ? '#3b82f6' : '#ec4899';

  // 拖拽开始
  const handlePointerDown = (e) => {
    e.stopPropagation();
    setIsDragging(true);
    setDragStart({
      x: e.point.x,
      z: e.point.z,
      memberX: member.x,
      memberZ: member.z
    });
    onSelect && onSelect(member.id);
  };

  // 拖拽中
  const handlePointerMove = (e) => {
    if (!isDragging || !dragStart) return;
    const deltaX = e.point.x - dragStart.x;
    const deltaZ = e.point.z - dragStart.z;
    let newX = dragStart.memberX + deltaX;
    let newZ = dragStart.memberZ + deltaZ;

    // 吸附网格（以0.5为单位）
    newX = Math.round(newX * 2) / 2;
    newZ = Math.round(newZ * 2) / 2;

    // 限制边界
    const clampedX = Math.max(-10, Math.min(10, newX));
    const clampedZ = Math.max(-10, Math.min(10, newZ));
    onUpdate(member.id, { x: clampedX, z: clampedZ });
  };

  // 拖拽结束
  const handlePointerUp = () => {
    setIsDragging(false);
    setDragStart(null);
  };

  // 滚轮缩放
  const handleWheel = (e) => {
    e.stopPropagation();
    const delta = e.deltaY > 0 ? -0.1 : 0.1;
    const newSize = Math.max(0.5, Math.min(3, size + delta));
    onUpdate(member.id, { size: newSize });
  };

  // 双击旋转
  const handleDoubleClick = (e) => {
    e.stopPropagation();
    const newRotation = ((member.rotation || 0) + Math.PI / 2) % (Math.PI * 2);
    onUpdate(member.id, { rotation: newRotation });
  };

  return (
    <group
      ref={meshRef}
      position={[member.x, size * 0.5, member.z]}
      rotation={[0, member.rotation || 0, 0]}
      onPointerDown={handlePointerDown}
      onPointerMove={handlePointerMove}
      onPointerUp={handlePointerUp}
      onWheel={handleWheel}
      onDoubleClick={handleDoubleClick}
    >
      {/* 头部 */}
      <mesh position={[0, size * 0.6, 0]}>
        <sphereGeometry args={[size * 0.3, 16, 16]} />
        <meshStandardMaterial color={color} />
      </mesh>

      {/* 身体 */}
      <mesh position={[0, size * 0.2, 0]}>
        <coneGeometry args={[size * 0.4, size * 0.8, 8]} />
        <meshStandardMaterial color={color} />
      </mesh>

      {/* 选中高亮环 */}
      {isSelected && (
        <mesh position={[0, -0.05, 0]} rotation={[-Math.PI / 2, 0, 0]}>
          <ringGeometry args={[size * 0.45, size * 0.5, 32]} />
          <meshBasicMaterial color="#6366f1" side={THREE.DoubleSide} />
        </mesh>
      )}

      {/* 死亡叉号 */}
      {member.isDeceased && (
        <mesh position={[0, size * 0.85, 0]}>
          <textGeometry args={['✖', { size: size * 0.2, height: 0.05 }]} />
          <meshBasicMaterial color="#6b7280" />
        </mesh>
      )}
    </group>
  );
};

const Canvas3D = ({ members, onUpdateMember, selectedMember, onSelectMember }) => {
  return (
    <div style={{ width: '100%', height: '600px' }}>
      <Canvas camera={{ position: [0, 15, 15], fov: 50 }} shadows>
        {/* 光照 */}
        <ambientLight intensity={0.4} />
        <directionalLight position={[10, 10, 5]} intensity={1} castShadow />

        {/* 视角控制 */}
        <OrbitControls
          enableZoom
          enablePan
          enableRotate
          maxPolarAngle={Math.PI / 2}
          minDistance={5}
          maxDistance={30}
        />

        {/* 网格辅助线 */}
        <Grid
          args={[20, 20]}
          cellSize={1}
          cellThickness={0.5}
          cellColor="#6b7280"
          sectionSize={5}
          sectionThickness={1}
          sectionColor="#374151"
          fadeDistance={30}
          fadeStrength={1}
          infiniteGrid
        />

        {/* 成员渲染 */}
        {members.map(member => (
          <FamilyMember3D
            key={member.id}
            member={member}
            onUpdate={onUpdateMember}
            isSelected={selectedMember === member.id}
            onSelect={onSelectMember}
          />
        ))}

        {/* 地面 */}
        <mesh rotation={[-Math.PI / 2, 0, 0]} position={[0, -0.1, 0]} receiveShadow>
          <planeGeometry args={[25, 25]} />
          <meshStandardMaterial color="#f9fafb" />
        </mesh>
      </Canvas>
    </div>
  );
};

export default Canvas3D;
