import React from 'react';
import { useDrop } from 'react-dnd';
import FamilyMember from './FamilyMember';
import './index.css';

const Canvas = ({ members, updateMemberPosition }) => {
  const [, drop] = useDrop({
    accept: 'MEMBER',
    drop: (item, monitor) => {
      const offset = monitor.getSourceClientOffset();
      const canvas = document.getElementById('canvas-area');
      const canvasRect = canvas.getBoundingClientRect();
      if (offset && canvasRect) {
        let x = offset.x - canvasRect.left;
        let y = offset.y - canvasRect.top;
        // 限制成员不能超出画布
        const memberSize = 90; // 与FamilyMember最小宽高一致
        x = Math.max(0, Math.min(x, canvasRect.width - memberSize));
        y = Math.max(0, Math.min(y, canvasRect.height - memberSize));
        updateMemberPosition(item.id, x, y);
      }
    },
  });

  return (
    <div
      id="canvas-area"
      ref={drop}
      className="relative w-full h-[500px] bg-white border-4 border-blue-200 rounded-xl mb-6 overflow-hidden shadow-inner"
      style={{
        minHeight: 400,
        backgroundImage: 'radial-gradient(circle, #e0f2fe 1px, transparent 1px)',
        backgroundSize: '20px 20px',
      }}
    >
      {members.length === 0 ? (
        <div className="text-blue-400 text-xl font-semibold select-none absolute left-1/2 top-1/2 -translate-x-1/2 -translate-y-1/2">
          请添加家庭成员并拖动到画布
        </div>
      ) : (
        members.map(member => (
          <FamilyMember
            key={member.id}
            member={member}
          />
        ))
      )}
    </div>
  );
};

export default Canvas;
