import React, { useState, useEffect, useRef } from 'react';
import Canvas3D from './Canvas3D';
import axios from 'axios';

const PRESET_ROLES = [
  { role: 'Ëá™Â∑±', icon: 'üßë' },
  { role: 'Áà∂‰∫≤', icon: 'üë®' },
  { role: 'ÊØç‰∫≤', icon: 'üë©' },
  { role: '‰∏àÂ§´', icon: 'üë®' },
  { role: 'Â¶ªÂ≠ê', icon: 'üë©' },
  { role: 'ÂÑøÂ≠ê', icon: 'üë¶' },
  { role: 'Â•≥ÂÑø', icon: 'üëß' },
  { role: 'Â≠©Â≠ê', icon: 'üßí' },
  { role: 'Á•ñÁà∂', icon: 'üë¥' },
  { role: 'Á•ñÊØç', icon: 'üëµ' },
  { role: 'ÈÖçÂÅ∂', icon: 'üíë' },
  { role: 'Âì•Âì•', icon: 'üßë‚Äçü¶±' },
  { role: 'ÂßêÂßê', icon: 'üë©‚Äçü¶∞' },
  { role: 'ÂºüÂºü', icon: 'üßë‚Äçüéì' },
  { role: 'Â¶πÂ¶π', icon: 'üëß' },
  { role: 'ÁªßÁà∂', icon: 'üßî' },
  { role: 'ÁªßÊØç', icon: 'üë©‚Äçü¶≥' },
  { role: 'ÂÖªÁà∂', icon: 'üßì' },
  { role: 'ÂÖªÊØç', icon: 'üëµ' },
  { role: 'ÊúãÂèã', icon: 'ü§ù' },
  { role: 'ÂÆ†Áâ©', icon: 'üê∂' },
];

export default function App() {
  const [members, setMembers] = useState(() => {
    const saved = localStorage.getItem('members');
    return saved ? JSON.parse(saved) : [];
  });
  const [name, setName] = useState('');
  const [role, setRole] = useState('');
  const [icon, setIcon] = useState('üßë');
  const [isDeceased, setIsDeceased] = useState(false);
  const [analysis, setAnalysis] = useState('');
  const [loading, setLoading] = useState(false);
  const [dragCompleted, setDragCompleted] = useState(false);
  const [selectedMember, setSelectedMember] = useState(null);
  const [gender, setGender] = useState('');

  useEffect(() => {
    localStorage.setItem('members', JSON.stringify(members));
  }, [members]);

  const addMember = () => {
    if (!name || !role || !gender) return;

    // ‰∏∫3DÁ©∫Èó¥ÁîüÊàêÈöèÊú∫‰ΩçÁΩÆ
    const x = (Math.random() - 0.5) * 16; // -8 Âà∞ 8
    const z = (Math.random() - 0.5) * 16; // -8 Âà∞ 8

    setMembers([
      ...members,
      {
        id: Date.now(),
        name,
        role,
        icon,
        isDeceased,
        gender,
        x: x,
        z: z,
        size: 1,
        rotation: 0
      }
    ]);
    setName('');
    setRole('');
    setIcon('üßë');
    setIsDeceased(false);
    setGender('');
  };

  const quickAdd = preset => {
    setRole(preset.role);
    setIcon(preset.icon);
    setName('');
  };

  const onUpdateMember = (id, changes) => {
    setMembers(members => members.map(m => m.id === id ? { ...m, ...changes } : m));
  };

  const generateDescription = () => {
    return members.map(m => {
      const status = m.isDeceased ? 'ÔºàÂ∑≤ÊïÖÔºâ' : '';
      return `${m.name}Ôºà${m.role}Ôºâ${status}‰Ωç‰∫é3DÂùêÊ†á(${m.x.toFixed(1)}, ${m.z.toFixed(1)})ÔºåÂ§ßÂ∞è${m.size || 1}`;
    }).join('Ôºå');
  };

  const analyze = async () => {
    setLoading(true);
    setAnalysis('');
    try {
      const description = generateDescription();
      const res = await axios.post('http://127.0.0.1:5000/analyze', { description });
      setAnalysis(res.data.analysis);
    } catch {
      setAnalysis('‚ö†Ô∏è ÂàÜÊûêÂ§±Ë¥•ÔºåËØ∑Á®çÂêéÈáçËØï„ÄÇ');
    }
    setLoading(false);
  };

  const clearMembers = () => {
    setMembers([]);
    setAnalysis('');
    setDragCompleted(false);
    setSelectedMember(null);
  };

  const handleDragComplete = async () => {
    setDragCompleted(true);
    setLoading(true);
    setAnalysis('');

    try {
      // 3DÁâàÊú¨ÊöÇÊó∂‰ΩøÁî®ÊñáÂ≠óÂàÜÊûê
      const description = generateDescription();
      const res = await axios.post('http://127.0.0.1:5000/analyze', { description });
      setAnalysis(res.data.analysis);
    } catch (error) {
      console.error('ÂàÜÊûêÂ§±Ë¥•:', error);
      setAnalysis('‚ö†Ô∏è ÂàÜÊûêÂ§±Ë¥•ÔºåËØ∑Á®çÂêéÈáçËØï„ÄÇ');
    }

    setLoading(false);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-blue-100 py-6 px-4 sm:px-6">
      <div className="max-w-7xl mx-auto bg-white rounded-3xl shadow-2xl p-6 sm:p-10 border border-blue-100">
        <h1 className="text-4xl font-bold text-blue-700 text-center mb-6 drop-shadow-sm">3DÂÆ∂Â∫≠ÊéíÂàóÂàÜÊûê</h1>

        {/* Ë°®ÂçïÂå∫Âüü */}
        <div className="bg-blue-50 border border-blue-200 rounded-3xl shadow-lg p-8 mb-8">
          <div className="flex flex-wrap justify-center gap-3 mb-6">
            {PRESET_ROLES.map(preset => (
              <button
                key={preset.role}
                onClick={() => quickAdd(preset)}
                className="bg-blue-100 hover:bg-blue-300 text-blue-800 px-4 py-2 rounded-2xl text-2xl font-bold flex items-center gap-1 border border-blue-300 shadow-sm transition-transform hover:scale-105"
              >
                <span className="text-3xl">{preset.icon}</span>{preset.role}
              </button>
