import React, { useState } from 'react';
import Canvas from './Canvas';
import axios from 'axios';

const PRESET_ROLES = [
  { role: '自己', icon: '🧑' },
  { role: '父亲', icon: '👨' },
  { role: '母亲', icon: '👩' },
  { role: '孩子', icon: '🧒' },
  { role: '祖父', icon: '👴' },
  { role: '祖母', icon: '👵' },
];

function App() {
  const [members, setMembers] = useState(() => {
    const saved = localStorage.getItem('members');
    return saved ? JSON.parse(saved) : [];
  });
  const [name, setName] = useState('');
  const [role, setRole] = useState('');
  const [icon, setIcon] = useState('🧑');
  const [analysis, setAnalysis] = useState('');
  const [loading, setLoading] = useState(false);

  React.useEffect(() => {
    localStorage.setItem('members', JSON.stringify(members));
  }, [members]);

  const addMember = () => {
    if (!name || !role) return;
    setMembers([...members, { id: Date.now(), name, role, icon, x: 100, y: 100 }]);
    setName('');
    setRole('');
    setIcon('🧑');
  };

  const quickAdd = (preset) => {
    setRole(preset.role);
    setIcon(preset.icon);
    setName('');
  };

  const updateMemberPosition = (id, x, y) => {
    setMembers(members.map(m => m.id === id ? { ...m, x, y } : m));
  };

  const generateDescription = () => {
    if (members.length === 0) return '';
    return members.map(m => `${m.name}（${m.role}）位于(${m.x}, ${m.y})`).join('，');
  };

  const analyze = async () => {
    setLoading(true);
    setAnalysis('');
    try {
      const description = generateDescription();
      const res = await axios.post('http://127.0.0.1:5000/analyze', { description });
      setAnalysis(res.data.analysis);
    } catch (e) {
      setAnalysis('分析失败，请稍后重试。');
    }
    setLoading(false);
  };

  const clearMembers = () => {
    setMembers([]);
    setAnalysis('');
  };

  return (
    <div className="min-h-screen bg-blue-50 p-0 sm:p-4">
      <div className="max-w-3xl mx-auto bg-white/95 rounded-2xl shadow-2xl p-6 sm:p-10 mt-0 sm:mt-8 border border-blue-100">
        <h1 className="text-3xl sm:text-4xl font-extrabold text-blue-700 mb-6 text-center tracking-tight drop-shadow">家庭成员排列分析</h1>
        <div className="flex gap-2 mb-3 flex-wrap justify-center">
          {PRESET_ROLES.map(preset => (
            <button
              key={preset.role}
              className="bg-blue-100 hover:bg-blue-300 text-blue-700 font-semibold px-3 py-1 rounded-xl flex items-center gap-1 shadow-sm border border-blue-200 transition"
              onClick={() => quickAdd(preset)}
            >
              <span className="text-xl">{preset.icon}</span>{preset.role}
            </button>
          ))}
        </div>
        <div className="flex gap-4 mb-6 items-center justify-center flex-wrap">
          <input
            className="border-2 border-blue-200 p-3 rounded-lg flex-1 min-w-[150px] focus:outline-none focus:border-blue-400 transition"
            placeholder="姓名"
            value={name}
            onChange={e => setName(e.target.value)}
          />
          <input
            className="border-2 border-blue-200 p-3 rounded-lg flex-1 min-w-[150px] focus:outline-none focus:border-blue-400 transition"
            placeholder="关系"
            value={role}
            onChange={e => setRole(e.target.value)}
          />
          <select
            className="border-2 border-blue-200 p-3 rounded-lg focus:outline-none focus:border-blue-400 transition text-2xl"
            value={icon}
            onChange={e => setIcon(e.target.value)}
          >
            {PRESET_ROLES.map(preset => (
              <option key={preset.role} value={preset.icon} className="text-xl">{preset.icon}</option>
            ))}
          </select>
          <button className="bg-blue-500 hover:bg-blue-600 text-white px-8 py-3 rounded-xl font-bold shadow transition" onClick={addMember}>添加</button>
        </div>
        <Canvas members={members} updateMemberPosition={updateMemberPosition} />
        <div className="flex gap-2 mt-2 justify-center">
          <button
            className="bg-gradient-to-r from-green-400 to-blue-400 hover:from-green-500 hover:to-blue-500 text-white px-8 py-2 rounded-xl font-bold shadow-md transition"
            onClick={analyze}
            disabled={loading || members.length === 0}
          >
            {loading ? '分析中...' : '分析'}
          </button>
          <button
            className="bg-gray-200 hover:bg-gray-300 text-gray-700 px-6 py-2 rounded-xl font-semibold shadow border border-gray-300 transition"
            onClick={clearMembers}
            disabled={members.length === 0}
          >
            清空
          </button>
        </div>
        {analysis && (
          <div className="mt-8 p-6 bg-blue-50 rounded-xl border border-blue-200 shadow-inner">
            <div className="font-bold text-blue-700 mb-2 text-lg">分析结果：</div>
            <div className="text-gray-800 leading-relaxed whitespace-pre-line">{analysis}</div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
