import React, { useState } from 'react';
import { useDrag } from 'react-dnd';

const maleRoles = ['父亲', '祖父', '哥哥', '弟弟', '儿子', '继父', '养父'];
const femaleRoles = ['母亲', '祖母', '姐姐', '妹妹', '女儿', '继母', '养母'];
const childRoles = ['儿子', '女儿', '孩子'];

const directionOrder = ['up', 'right', 'down', 'left'];
const directionAngle = { up: 0, right: 90, down: 180, left: 270 };

const FamilyMember = ({ member, onUpdate }) => {
  const [{ isDragging }, drag] = useDrag({
    type: 'MEMBER',
    item: { id: member.id },
    collect: monitor => ({
      isDragging: monitor.isDragging(),
    }),
  });

  // 判断边框形状
  let borderRadius = 20;
  if (maleRoles.includes(member.role)) borderRadius = 8;
  if (femaleRoles.includes(member.role)) borderRadius = 9999;

  // 框大小
  const width = member.width || (childRoles.includes(member.role) ? 60 : 90);
  const height = member.height || (childRoles.includes(member.role) ? 60 : 90);

  // 悬停状态
  const [hovered, setHovered] = useState(false);
  // 缩放状态
  const [resizing, setResizing] = useState(false);
  const [resizeStart, setResizeStart] = useState(null);

  // 旋转朝向
  const handleRotate = e => {
    e.stopPropagation();
    const idx = directionOrder.indexOf(member.direction || 'up');
    const next = directionOrder[(idx + 1) % 4];
    onUpdate && onUpdate(member.id, { direction: next });
  };

  // 缩放逻辑
  const handleResizeMouseDown = e => {
    e.stopPropagation();
    setResizing(true);
    setResizeStart({ x: e.clientX, y: e.clientY, width, height });
    document.body.style.cursor = 'nwse-resize';
  };
  React.useEffect(() => {
    if (!resizing) return;
    const handleMove = e => {
      const dx = e.clientX - resizeStart.x;
      const dy = e.clientY - resizeStart.y;
      let newWidth = Math.max(40, resizeStart.width + dx);
      let newHeight = Math.max(40, resizeStart.height + dy);
      onUpdate && onUpdate(member.id, { width: newWidth, height: newHeight });
    };
    const handleUp = () => {
      setResizing(false);
      document.body.style.cursor = '';
    };
    window.addEventListener('mousemove', handleMove);
    window.addEventListener('mouseup', handleUp);
    return () => {
      window.removeEventListener('mousemove', handleMove);
      window.removeEventListener('mouseup', handleUp);
    };
  }, [resizing, resizeStart, onUpdate, member.id]);

  // 渲染三角形
  const renderDirectionTriangle = () => (
    <svg
      width="18" height="18"
      style={{
        position: 'absolute',
        top: 2,
        left: '50%',
        transform: `translateX(-50%) rotate(${directionAngle[member.direction || 'up']}deg)`,
        zIndex: 10,
        pointerEvents: 'none',
      }}
    >
      <polygon points="9,0 18,18 0,18" fill="#6366f1" />
    </svg>
  );

  // 渲染旋转按钮
  const renderRotateButton = () => (
    <button
      onClick={handleRotate}
      style={{
        position: 'absolute',
        top: 4,
        right: 4,
        zIndex: 20,
        background: 'rgba(99,102,241,0.12)',
        border: 'none',
        borderRadius: '50%',
        width: 24,
        height: 24,
        cursor: 'pointer',
        display: hovered ? 'block' : 'none',
      }}
      title="旋转朝向"
    >
      <svg width="16" height="16" viewBox="0 0 16 16"><path d="M8 2v2.5a.5.5 0 0 0 .5.5H11" stroke="#6366f1" strokeWidth="1.5" fill="none"/><path d="M13.5 8A5.5 5.5 0 1 1 8 2" stroke="#6366f1" strokeWidth="1.5" fill="none"/></svg>
    </button>
  );

  // 渲染缩放手柄
  const renderResizeHandle = () => (
    <div
      onMouseDown={handleResizeMouseDown}
      style={{
        position: 'absolute',
        right: -8,
        bottom: -8,
        width: 18,
        height: 18,
        background: '#fff',
        border: '2px solid #6366f1',
        borderRadius: 6,
        cursor: 'nwse-resize',
        zIndex: 30,
        boxShadow: '0 1px 4px rgba(0,0,0,0.08)',
        display: hovered ? 'block' : 'none',
      }}
      title="缩放"
    >
      <svg width="14" height="14" style={{margin:2}}><polyline points="2,12 12,12 12,2" fill="none" stroke="#6366f1" strokeWidth="2"/></svg>
    </div>
  );

  return (
    <div
      ref={drag}
      style={{
        position: 'absolute',
        left: member.x,
        top: member.y,
        width,
        height,
        minWidth: 40,
        minHeight: 40,
        cursor: isDragging ? 'grabbing' : 'move',
        opacity: isDragging ? 0.5 : 1,
        zIndex: 2000,
        boxShadow: '0 6px 20px 0 rgba(0,0,0,0.15)',
        transition: 'box-shadow 0.2s, transform 0.2s',
        background: 'rgba(255,255,255,0.98)',
        border: '3px solid #a5b4fc',
        borderRadius,
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        transform: isDragging ? 'scale(1.05)' : 'scale(1)',
        userSelect: 'none',
      }}
      className="hover:shadow-xl group select-none"
      onMouseEnter={() => setHovered(true)}
      onMouseLeave={() => setHovered(false)}
    >
      {renderDirectionTriangle()}
      {renderRotateButton()}
      {/* 死亡叉号 */}
      {member.isDeceased && (
        <div 
          style={{
            position: 'absolute',
            top: '50%',
            left: '50%',
            transform: 'translate(-50%, -50%)',
            fontSize: width * 1.2,
            color: '#6b7280',
            fontWeight: 'bold',
            zIndex: 2001,
            textShadow: '2px 2px 4px rgba(0,0,0,0.3)',
            opacity: 0.6,
            pointerEvents: 'none',
          }}
        >
          ✕
        </div>
      )}
      <div className="text-5xl mb-1 drop-shadow-sm group-hover:scale-110 transition-transform duration-200" style={{fontSize: width/2}}>
        {member.icon || '🧑'}
      </div>
      <div className="font-bold text-base text-gray-800 mb-0.5" style={{fontSize: width/5}}>{member.name}</div>
      <div className="text-xs text-blue-500 font-semibold tracking-wide" style={{fontSize: width/6}}>{member.role}</div>
      {renderResizeHandle()}
    </div>
  );
};

export default FamilyMember;
