import React from 'react';
import { useDrag } from 'react-dnd';
import { useDrop } from 'react-dnd';

const FamilyMember = ({ member, updateMemberPosition }) => {
  const [{ isDragging }, drag] = useDrag({
    type: 'MEMBER',
    item: { id: member.id, x: member.x, y: member.y },
    collect: monitor => ({
      isDragging: monitor.isDragging(),
    }),
    end: (item, monitor) => {
      const offset = monitor.getDifferenceFromInitialOffset();
      if (offset) {
        updateMemberPosition(
          member.id,
          Math.max(0, member.x + offset.x),
          Math.max(0, member.y + offset.y)
        );
      }
    },
  });

  const [, drop] = useDrop(() => ({ accept: 'MEMBER' }));

  return (
    <div
      ref={drag}
      style={{
        position: 'absolute',
        left: member.x,
        top: member.y,
        minWidth: 60,
        cursor: 'move',
        opacity: isDragging ? 0.5 : 1,
        zIndex: 2000
      }}
      className="px-3 py-2 rounded shadow bg-white border border-gray-300 text-center select-none transition-all"
    >
      <div className="text-2xl mb-1">{member.icon || 'ðŸ§‘'}</div>
      <div className="font-bold">{member.name}</div>
      <div className="text-xs text-gray-500">{member.role}</div>
    </div>
  );
};

export default FamilyMember;
