import React from 'react';
import { useDrop } from 'react-dnd';
import FamilyMember from './FamilyMember';
import './index.css';

const Canvas = ({ members, updateMemberPosition }) => {
  const [, drop] = useDrop({
    accept: 'MEMBER',
    drop: (item, monitor) => {
      const offset = monitor.getSourceClientOffset();
      const canvasRect = document.getElementById('canvas-area').getBoundingClientRect();
      if (offset && canvasRect) {
        const x = offset.x - canvasRect.left;
        const y = offset.y - canvasRect.top;
        updateMemberPosition(item.id, x, y);
      }
    },
  });

  return (
    <div
      id="canvas-area"
      ref={drop}
      className="relative w-full h-[500px] bg-white border-4 border-blue-200 rounded-xl mb-6 overflow-hidden shadow-lg"
      style={{ minHeight: 400 }}
    >
      {members.length === 0 ? (
        <div className="text-blue-400 text-lg select-none absolute left-1/2 top-1/2 -translate-x-1/2 -translate-y-1/2">请添加家庭成员并拖动到画布</div>
      ) : (
        members.map(member => (
          <FamilyMember
            key={member.id}
            member={member}
          />
        ))
      )}
    </div>
  );
};

export default Canvas;
