import React from 'react';
import { useDrop } from 'react-dnd';
import FamilyMember from './FamilyMember';
import './index.css';

const Canvas = ({ members, updateMemberPosition }) => {
  const [, drop] = useDrop(() => ({ accept: 'MEMBER' }));

  return (
    <div
      ref={drop}
      style={{
        minHeight: 300,
        height: 400,
        width: '100%',
        background: 'red',
        border: '4px solid black',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        position: 'relative',
        zIndex: 1000
      }}
    >
      <div style={{ color: 'white', fontSize: 24 }}>画布测试区域</div>
      {members.length === 0 ? null : (
        members.map(member => (
          <FamilyMember
            key={member.id}
            member={member}
            updateMemberPosition={updateMemberPosition}
          />
        ))
      )}
    </div>
  );
};

export default Canvas;
