import React from 'react';
import { useDrop } from 'react-dnd';
// import FamilyMember from './FamilyMember'; // 假设 FamilyMember 组件已正确导入
// 如果 FamilyMember 组件未提供，这里提供一个简化的占位符，以便 Canvas 组件可以独立运行
// 在实际项目中，请确保 FamilyMember.js 文件存在并正确导入。
const FamilyMember = ({ member }) => {
  // 假设 FamilyMember 组件使用 transform: translate(-50%, -50%) 来居中其内容
  // 这样 member.x 和 member.y 就代表成员的中心坐标
  return (
    <div
      className="absolute bg-blue-200 text-blue-800 p-4 rounded-full shadow-lg cursor-grab active:cursor-grabbing transform -translate-x-1/2 -translate-y-1/2 flex flex-col items-center justify-center transition-all duration-100 ease-out"
      style={{
        left: member.x,
        top: member.y,
        width: '90px', // MEMBER_SIZE
        height: '90px', // MEMBER_SIZE
        fontSize: '1.5rem', // 字体大小
        minWidth: '80px',
        minHeight: '80px'
      }}
    >
      <span className="text-5xl mb-1">{member.icon}</span>
      <span className="font-bold text-xl">{member.name}</span>
      <span className="text-lg text-blue-700">{member.role}</span>
    </div>
  );
};


// MEMBER_SIZE 代表成员元素的完整宽度/高度，用于计算重叠和边界
const MEMBER_SIZE = 90;
const MIN_DISTANCE = 0; // 成员之间允许的最小距离，0 表示可以接触

/**
 * 获取一个不与其他成员重叠的位置，并确保在画布边界内。
 * 这里的 x, y 和 m.x, m.y 都被视为成员的中心坐标。
 * @param {number} x - 目标X坐标 (中心)
 * @param {number} y - 目标Y坐标 (中心)
 * @param {Array} members - 所有家庭成员数组
 * @param {number} currentId - 当前正在拖动的成员ID
 * @param {number} canvasWidth - 画布宽度
 * @param {number} canvasHeight - 画布高度
 * @returns {{x: number, y: number}} 安全的X, Y坐标
 */
function getNonOverlappingPosition(x, y, members, currentId, canvasWidth, canvasHeight) {
  let safeX = x;
  let safeY = y;
  let tries = 0;
  const halfMember = MEMBER_SIZE / 2;

  while (tries < 30) { // 尝试30次以寻找不重叠的位置
    const isOverlapping = members.some(
      m =>
        m.id !== currentId &&
        // 检查两个成员的中心点距离是否小于一个成员的完整尺寸，即它们重叠
        Math.abs(m.x - safeX) < MEMBER_SIZE - MIN_DISTANCE &&
        Math.abs(m.y - safeY) < MEMBER_SIZE - MIN_DISTANCE
    );

    if (!isOverlapping) break; // 如果不重叠，则找到安全位置

    // 如果重叠，微调位置并确保仍在画布内
    // 每次尝试向右下角移动一点，避免死循环
    safeX = Math.max(halfMember, Math.min(safeX + 15, canvasWidth - halfMember));
    safeY = Math.max(halfMember, Math.min(safeY + 15, canvasHeight - halfMember));
    tries++;
  }

  // 最终确保位置在画布边界内
  safeX = Math.max(halfMember, Math.min(safeX, canvasWidth - halfMember));
  safeY = Math.max(halfMember, Math.min(safeY, canvasHeight - halfMember));

  return { x: safeX, y: safeY };
}

const Canvas = ({ members, updateMemberPosition }) => {
  const [, drop] = useDrop({
    accept: 'MEMBER',
    drop: (item, monitor) => {
      const clientOffset = monitor.getClientOffset(); // 鼠标在视口中的坐标
      const canvas = document.getElementById('canvas-area');

      if (!clientOffset || !canvas) return;

      // 获取画布相对于视口的位置和尺寸
      const canvasRect = canvas.getBoundingClientRect();
      const canvasWidth = canvasRect.width;
      const canvasHeight = canvasRect.height;
      const halfMember = MEMBER_SIZE / 2;

      // 计算鼠标在画布内的相对位置
      // 这将是成员的中心点坐标
      let x = clientOffset.x - canvasRect.left;
      let y = clientOffset.y - canvasRect.top;

      // 确保成员的中心点完全在画布内
      x = Math.max(halfMember, Math.min(x, canvasWidth - halfMember));
      y = Math.max(halfMember, Math.min(y, canvasHeight - halfMember));

      // 防止重叠，并再次确保在画布内
      const { x: safeX, y: safeY } = getNonOverlappingPosition(
        x, y, members, item.id, canvasWidth, canvasHeight
      );

      updateMemberPosition(item.id, safeX, safeY);
    }
  });

  return (
    <div
      id="canvas-area"
      ref={drop}
      className="mx-auto relative bg-white border-4 border-blue-300 rounded-xl overflow-hidden shadow-inner select-none w-full h-[500px] md:h-[600px] lg:h-[700px]" // 响应式高度
      style={{
        // 使用 Tailwind CSS 替代固定 width 和 height
        // width: 1200, // 移除固定宽度
        // height: 500, // 移除固定高度
        backgroundImage: `
          radial-gradient(circle,rgb(188, 175, 200) 1px, transparent 1px),
          linear-gradient(#f3f4f6,rgb(255, 255, 255))
        `,
        backgroundSize: '20px 20px, 100% 100%',
        backgroundPosition: '0 0, 0 0',
        boxSizing: 'border-box', // 确保边框包含在宽高中
      }}
    >
      {members.length === 0 ? (
        <div className="absolute left-1/2 top-1/2 -translate-x-1/2 -translate-y-1/2 text-blue-400 text-3xl font-semibold select-none">
          请添加家庭成员并拖动到画布
        </div>
      ) : (
        members.map(member => (
          // 确保 FamilyMember 组件能够正确接收并使用 member.x 和 member.y 作为中心坐标
          <FamilyMember key={member.id} member={member} />
        ))
      )}
    </div>
  );
};

export default Canvas;
