import React, { useRef } from 'react';
import { useDrop } from 'react-dnd';
import FamilyMember from './FamilyMember';
import './index.css';

const MEMBER_SIZE = 90;
const MIN_DISTANCE = 10; // 最小间距

function getNonOverlappingPosition(x, y, members, currentId, canvasWidth, canvasHeight) {
  let safeX = x, safeY = y;
  let tries = 0;
  while (tries < 30) {
    const overlap = members.some(m => m.id !== currentId && Math.abs(m.x - safeX) < MEMBER_SIZE - MIN_DISTANCE && Math.abs(m.y - safeY) < MEMBER_SIZE - MIN_DISTANCE);
    if (!overlap) break;
    safeX += 15;
    safeY += 15;
    safeX = Math.max(0, Math.min(safeX, canvasWidth - MEMBER_SIZE));
    safeY = Math.max(0, Math.min(safeY, canvasHeight - MEMBER_SIZE));
    tries++;
  }
  return { x: safeX, y: safeY };
}

const Canvas = ({ members, updateMemberPosition }) => {
  // 记录每次拖拽的偏移量
  const dragOffsetRef = useRef({});

  const [, drop] = useDrop({
    accept: 'MEMBER',
    hover: (item, monitor) => {
      // 记录拖拽起始时鼠标与成员左上角的偏移
      if (!dragOffsetRef.current[item.id] && monitor.isOver({ shallow: true })) {
        const initialClientOffset = monitor.getInitialClientOffset();
        const initialSourceClientOffset = monitor.getInitialSourceClientOffset();
        if (initialClientOffset && initialSourceClientOffset) {
          dragOffsetRef.current[item.id] = {
            offsetX: initialClientOffset.x - initialSourceClientOffset.x,
            offsetY: initialClientOffset.y - initialSourceClientOffset.y,
          };
        }
      }
    },
    drop: (item, monitor) => {
      const clientOffset = monitor.getClientOffset();
      const canvas = document.getElementById('canvas-area');
      const canvasRect = canvas.getBoundingClientRect();
      if (clientOffset && canvasRect) {
        // 以拖拽起始点为参考，保证鼠标点始终在成员卡片中心
        let offsetX = MEMBER_SIZE / 2;
        let offsetY = MEMBER_SIZE / 2;
        if (dragOffsetRef.current[item.id]) {
          offsetX = dragOffsetRef.current[item.id].offsetX;
          offsetY = dragOffsetRef.current[item.id].offsetY;
        }
        let x = clientOffset.x - canvasRect.left - offsetX;
        let y = clientOffset.y - canvasRect.top - offsetY;
        x = Math.max(0, Math.min(x, canvasRect.width - MEMBER_SIZE));
        y = Math.max(0, Math.min(y, canvasRect.height - MEMBER_SIZE));
        const { x: safeX, y: safeY } = getNonOverlappingPosition(x, y, members, item.id, canvasRect.width, canvasRect.height);
        updateMemberPosition(item.id, safeX, safeY);
        // 拖拽完成后清除偏移记录
        delete dragOffsetRef.current[item.id];
      }
    },
  });

  return (
    <div
      id="canvas-area"
      ref={drop}
      className="relative w-full h-[500px] bg-gray-100 border-4 border-blue-200 rounded-xl mb-6 overflow-hidden shadow-inner"
      style={{
        minHeight: 400,
        backgroundImage: 'radial-gradient(circle, #e0f2fe 1px, transparent 1px), linear-gradient(#f3f4f6, #f3f4f6)',
        backgroundSize: '20px 20px, 100% 100%',
        backgroundPosition: '0 0, 0 0',
      }}
    >
      {members.length === 0 ? (
        <div className="text-blue-400 text-xl font-semibold select-none absolute left-1/2 top-1/2 -translate-x-1/2 -translate-y-1/2">
          请添加家庭成员并拖动到画布
        </div>
      ) : (
        members.map(member => (
          <FamilyMember
            key={member.id}
            member={member}
          />
        ))
      )}
    </div>
  );
};

export default Canvas;
