import React, { useState, useEffect } from 'react';
import { DndProvider, useDrag, useDrop } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import axios from 'axios'; // ÂÅáËÆæÊÇ®‰ªçÁÑ∂ÈúÄË¶ÅaxiosÊù•Ê®°ÊãüÂêéÁ´ØÂàÜÊûê

// MEMBER_SIZE ‰ª£Ë°®ÊàêÂëòÂÖÉÁ¥†ÁöÑÂÆåÊï¥ÂÆΩÂ∫¶/È´òÂ∫¶ÔºåÁî®‰∫éËÆ°ÁÆóÈáçÂè†ÂíåËæπÁïå
const MEMBER_SIZE = 90;
const MIN_DISTANCE = 0; // ÊàêÂëò‰πãÈó¥ÂÖÅËÆ∏ÁöÑÊúÄÂ∞èË∑ùÁ¶ªÔºå0 Ë°®Á§∫ÂèØ‰ª•Êé•Ëß¶

// FamilyMember ÁªÑ‰ª∂ (‰ªé Canvas.js Â§çÂà∂ËÄåÊù•ÔºåÁ°Æ‰øù‰∏ÄËá¥ÊÄß)
const FamilyMember = ({ member }) => {
  // useDrag Èí©Â≠êÁî®‰∫é‰ΩøÁªÑ‰ª∂ÂèØÊãñÂä®
  const [{ isDragging }, drag] = useDrag(() => ({
    type: 'MEMBER', // ÊãñÂä®È°πÁöÑÁ±ªÂûã
    item: { id: member.id }, // ÊãñÂä®Êó∂‰º†ÈÄíÁöÑÊï∞ÊçÆ
    collect: (monitor) => ({
      isDragging: monitor.isDragging(), // Ê£ÄÊü•ÊòØÂê¶Ê≠£Âú®ÊãñÂä®
    }),
  }));

  return (
    <div
      ref={drag} // Â∞ÜÊãñÂä®Ê∫êËøûÊé•Âà∞ DOM ËäÇÁÇπ
      className="absolute bg-blue-200 text-blue-800 p-4 rounded-full shadow-lg cursor-grab active:cursor-grabbing transform -translate-x-1/2 -translate-y-1/2 flex flex-col items-center justify-center transition-all duration-100 ease-out"
      style={{
        left: member.x,
        top: member.y,
        width: `${MEMBER_SIZE}px`,
        height: `${MEMBER_SIZE}px`,
        fontSize: '1.5rem',
        minWidth: '80px',
        minHeight: '80px',
        opacity: isDragging ? 0.5 : 1, // ÊãñÂä®Êó∂Èôç‰Ωé‰∏çÈÄèÊòéÂ∫¶
        zIndex: isDragging ? 100 : 1, // ÊãñÂä®Êó∂ÊèêÂçáÂ±ÇÁ∫ß
      }}
    >
      <span className="text-5xl mb-1">{member.icon}</span>
      <span className="font-bold text-xl">{member.name}</span>
      <span className="text-lg text-blue-700">{member.role}</span>
    </div>
  );
};

/**
 * Ëé∑Âèñ‰∏Ä‰∏™‰∏ç‰∏éÂÖ∂‰ªñÊàêÂëòÈáçÂè†ÁöÑ‰ΩçÁΩÆÔºåÂπ∂Á°Æ‰øùÂú®ÁîªÂ∏ÉËæπÁïåÂÜÖ„ÄÇ
 * ËøôÈáåÁöÑ x, y Âíå m.x, m.y ÈÉΩË¢´ËßÜ‰∏∫ÊàêÂëòÁöÑ‰∏≠ÂøÉÂùêÊ†á„ÄÇ
 * @param {number} x - ÁõÆÊ†áXÂùêÊ†á (‰∏≠ÂøÉ)
 * @param {number} y - ÁõÆÊ†áYÂùêÊ†á (‰∏≠ÂøÉ)
 * @param {Array} members - ÊâÄÊúâÂÆ∂Â∫≠ÊàêÂëòÊï∞ÁªÑ
 * @param {number} currentId - ÂΩìÂâçÊ≠£Âú®ÊãñÂä®ÁöÑÊàêÂëòID
 * @param {number} canvasWidth - ÁîªÂ∏ÉÂÆΩÂ∫¶
 * @param {number} canvasHeight - ÁîªÂ∏ÉÈ´òÂ∫¶
 * @returns {{x: number, y: number}} ÂÆâÂÖ®ÁöÑX, YÂùêÊ†á
 */
function getNonOverlappingPosition(x, y, members, currentId, canvasWidth, canvasHeight) {
  let safeX = x;
  let safeY = y;
  let tries = 0;
  const halfMember = MEMBER_SIZE / 2;

  while (tries < 30) { // Â∞ùËØï30Ê¨°‰ª•ÂØªÊâæ‰∏çÈáçÂè†ÁöÑ‰ΩçÁΩÆ
    const isOverlapping = members.some(
      m =>
        m.id !== currentId &&
        // Ê£ÄÊü•‰∏§‰∏™ÊàêÂëòÁöÑ‰∏≠ÂøÉÁÇπË∑ùÁ¶ªÊòØÂê¶Â∞è‰∫é‰∏Ä‰∏™ÊàêÂëòÁöÑÂÆåÊï¥Â∞∫ÂØ∏ÔºåÂç≥ÂÆÉ‰ª¨ÈáçÂè†
        Math.abs(m.x - safeX) < MEMBER_SIZE - MIN_DISTANCE &&
        Math.abs(m.y - safeY) < MEMBER_SIZE - MIN_DISTANCE
    );

    if (!isOverlapping) break; // Â¶ÇÊûú‰∏çÈáçÂè†ÔºåÂàôÊâæÂà∞ÂÆâÂÖ®‰ΩçÁΩÆ

    // Â¶ÇÊûúÈáçÂè†ÔºåÂæÆË∞É‰ΩçÁΩÆÂπ∂Á°Æ‰øù‰ªçÂú®ÁîªÂ∏ÉÂÜÖ
    // ÊØèÊ¨°Â∞ùËØïÂêëÂè≥‰∏ãËßíÁßªÂä®‰∏ÄÁÇπÔºåÈÅøÂÖçÊ≠ªÂæ™ÁéØ
    safeX = Math.max(halfMember, Math.min(safeX + 15, canvasWidth - halfMember));
    safeY = Math.max(halfMember, Math.min(safeY + 15, canvasHeight - halfMember));
    tries++;
  }

  // ÊúÄÁªàÁ°Æ‰øù‰ΩçÁΩÆÂú®ÁîªÂ∏ÉËæπÁïåÂÜÖ
  safeX = Math.max(halfMember, Math.min(safeX, canvasWidth - halfMember));
  safeY = Math.max(halfMember, Math.min(safeY, canvasHeight - halfMember));

  return { x: safeX, y: safeY };
}


// Canvas ÁªÑ‰ª∂ (‰ªéÊÇ®Êèê‰æõÁöÑ Canvas.js Â§çÂà∂ËÄåÊù•ÔºåÁ°Æ‰øù‰∏ÄËá¥ÊÄß)
const Canvas = ({ members, updateMemberPosition }) => {
  const [, drop] = useDrop({
    accept: 'MEMBER',
    drop: (item, monitor) => {
      const clientOffset = monitor.getClientOffset(); // Èº†Ê†áÂú®ËßÜÂè£‰∏≠ÁöÑÂùêÊ†á
      const canvas = document.getElementById('canvas-area');

      if (!clientOffset || !canvas) return;

      // Ëé∑ÂèñÁîªÂ∏ÉÁõ∏ÂØπ‰∫éËßÜÂè£ÁöÑ‰ΩçÁΩÆÂíåÂ∞∫ÂØ∏
      const canvasRect = canvas.getBoundingClientRect();
      const canvasWidth = canvasRect.width;
      const canvasHeight = canvasRect.height;
      const halfMember = MEMBER_SIZE / 2;

      // ËÆ°ÁÆóÈº†Ê†áÂú®ÁîªÂ∏ÉÂÜÖÁöÑÁõ∏ÂØπ‰ΩçÁΩÆ
      // ËøôÂ∞ÜÊòØÊàêÂëòÁöÑ‰∏≠ÂøÉÁÇπÂùêÊ†á
      let x = clientOffset.x - canvasRect.left;
      let y = clientOffset.y - canvasRect.top;

      // Á°Æ‰øùÊàêÂëòÁöÑ‰∏≠ÂøÉÁÇπÂÆåÂÖ®Âú®ÁîªÂ∏ÉÂÜÖ
      x = Math.max(halfMember, Math.min(x, canvasWidth - halfMember));
      y = Math.max(halfMember, Math.min(y, canvasHeight - halfMember));

      // Èò≤Ê≠¢ÈáçÂè†ÔºåÂπ∂ÂÜçÊ¨°Á°Æ‰øùÂú®ÁîªÂ∏ÉÂÜÖ
      const { x: safeX, y: safeY } = getNonOverlappingPosition(
        x, y, members, item.id, canvasWidth, canvasHeight
      );

      updateMemberPosition(item.id, safeX, safeY);
    }
  });

  return (
    <div
      id="canvas-area"
      ref={drop}
      className="mx-auto relative bg-white border-4 border-blue-300 rounded-xl overflow-hidden shadow-inner select-none w-full h-[500px] md:h-[600px] lg:h-[700px]" // ÂìçÂ∫îÂºèÈ´òÂ∫¶
      style={{
        backgroundImage: `
          radial-gradient(circle,rgb(188, 175, 200) 1px, transparent 1px),
          linear-gradient(#f3f4f6,rgb(255, 255, 255))
        `,
        backgroundSize: '20px 20px, 100% 100%',
        backgroundPosition: '0 0, 0 0',
        boxSizing: 'border-box', // Á°Æ‰øùËæπÊ°ÜÂåÖÂê´Âú®ÂÆΩÈ´ò‰∏≠
      }}
    >
      {members.length === 0 ? (
        <div className="absolute left-1/2 top-1/2 -translate-x-1/2 -translate-y-1/2 text-blue-400 text-3xl font-semibold select-none">
          ËØ∑Ê∑ªÂä†ÂÆ∂Â∫≠ÊàêÂëòÂπ∂ÊãñÂä®Âà∞ÁîªÂ∏É
        </div>
      ) : (
        members.map(member => (
          <FamilyMember key={member.id} member={member} />
        ))
      )}
    </div>
  );
};


const PRESET_ROLES = [
  { role: 'Ëá™Â∑±', icon: 'üßë' },
  { role: 'Áà∂‰∫≤', icon: 'üë®' },
  { role: 'ÊØç‰∫≤', icon: 'üë©' },
  { role: 'Â≠©Â≠ê', icon: 'üßí' },
  { role: 'Á•ñÁà∂', icon: 'üë¥' },
  { role: 'Á•ñÊØç', icon: 'üëµ' },
  { role: 'ÈÖçÂÅ∂', icon: 'üíë' },
  { role: 'Âì•Âì•', icon: 'üßë‚Äçü¶±' },
  { role: 'ÂßêÂßê', icon: 'üë©‚Äçü¶∞' },
  { role: 'ÂºüÂºü', icon: 'üßë‚Äçüéì' },
  { role: 'Â¶πÂ¶π', icon: 'üëß' },
  { role: 'ÂÑøÂ≠ê', icon: 'üë¶' },
  { role: 'Â•≥ÂÑø', icon: 'üëß' },
  { role: 'ÁªßÁà∂', icon: 'üßî' },
  { role: 'ÁªßÊØç', icon: 'üë©‚Äçü¶≥' },
  { role: 'ÂÖªÁà∂', icon: 'üßì' },
  { role: 'ÂÖªÊØç', icon: 'üëµ' },
  { role: 'ÊúãÂèã', icon: 'ü§ù' },
  { role: 'ÂÆ†Áâ©', icon: 'üê∂' },
];

export default function App() {
  const [members, setMembers] = useState(() => {
    const saved = localStorage.getItem('members');
    return saved ? JSON.parse(saved) : [];
  });
  const [name, setName] = useState('');
  const [role, setRole] = useState('');
  const [icon, setIcon] = useState('üßë');
  const [analysis, setAnalysis] = useState('');
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    localStorage.setItem('members', JSON.stringify(members));
  }, [members]);

  const addMember = () => {
    if (!name || !role) return;
    // Set initial position for new members within the canvas boundaries
    // These values should ideally be dynamic based on actual canvas size
    const canvasWidth = document.getElementById('canvas-area')?.offsetWidth || 800;
    const canvasHeight = document.getElementById('canvas-area')?.offsetHeight || 400;
    const halfMember = MEMBER_SIZE / 2;

    const initialX = Math.random() * (canvasWidth - MEMBER_SIZE) + halfMember;
    const initialY = Math.random() * (canvasHeight - MEMBER_SIZE) + halfMember;

    setMembers([...members, { id: Date.now(), name, role, icon, x: initialX, y: initialY }]);
    setName('');
    setRole('');
    setIcon('üßë');
  };

  const quickAdd = preset => {
    setRole(preset.role);
    setIcon(preset.icon);
    setName(''); // Clear name when quick adding a role
  };

  const updateMemberPosition = (id, x, y) => {
    setMembers(members.map(m => (m.id === id ? { ...m, x, y } : m)));
  };

  const generateDescription = () => {
    return members.map(m => `${m.name}Ôºà${m.role}Ôºâ‰Ωç‰∫é(${Math.round(m.x)}, ${Math.round(m.y)})`).join('Ôºå');
  };

  const analyze = async () => {
    setLoading(true);
    setAnalysis('');
    try {
      const description = generateDescription();
      // Placeholder for actual API call. Replace with your backend endpoint.
      // For demonstration, we'll simulate an API call.
      // const res = await axios.post('http://127.0.0.1:5000/analyze', { description });
      // setAnalysis(res.data.analysis);

      // Simulated API call for demonstration
      await new Promise(resolve => setTimeout(resolve, 1500)); // Simulate network delay
      setAnalysis(`Ê†πÊçÆÊÇ®ÁöÑÊéíÂàóÔºåÂÆ∂Â∫≠ÊàêÂëòÔºö${description}„ÄÇËøôÂèØËÉΩÊöóÁ§∫‰∫ÜÊàêÂëòÈó¥ÁöÑ‰∫≤ÂØÜÁ®ãÂ∫¶„ÄÅÂΩ±ÂìçÂäõÊàñÂÖ≥Ê≥®ÁÑ¶ÁÇπ„ÄÇ‰æãÂ¶ÇÔºåÈù†Ëøë‰∏≠ÂøÉÁöÑÊàêÂëòÂèØËÉΩÊÑüËßâÊõ¥Ë¢´ÂÖ≥Ê≥®ÔºåËÄåËæπÁºòÁöÑÊàêÂëòÂèØËÉΩÊõ¥Áã¨Á´ã„ÄÇ`);

    } catch (error) {
      console.error("Analysis failed:", error);
      setAnalysis('‚ö†Ô∏è ÂàÜÊûêÂ§±Ë¥•ÔºåËØ∑Á®çÂêéÈáçËØï„ÄÇ');
    }
    setLoading(false);
  };

  const clearMembers = () => {
    setMembers([]);
    setAnalysis('');
  };

  return (
    <DndProvider backend={HTML5Backend}> {/* ‰ΩøÁî® DndProvider ÂåÖË£πÊï¥‰∏™Â∫îÁî® */}
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 py-8 px-4 sm:px-6 font-inter">
        <div className="max-w-6xl mx-auto bg-white rounded-3xl shadow-3xl p-8 sm:p-12 border border-blue-200">
          <h1 className="text-5xl font-extrabold text-blue-800 text-center mb-8 drop-shadow-lg tracking-tight">
            ÂÆ∂Â∫≠ÊàêÂëòÊéíÂàóÂàÜÊûêÂô®
          </h1>

          {/* Ë°®ÂçïÂå∫ÂüüÁæéÂåñ */}
          <div className="bg-blue-50 border border-blue-200 rounded-3xl shadow-xl p-8 mb-10">
            <h2 className="text-3xl font-bold text-blue-700 mb-6 text-center">Âø´ÈÄüÊ∑ªÂä†ÊàñËá™ÂÆö‰πâÊàêÂëò</h2>
            <div className="flex flex-wrap justify-center gap-4 mb-8">
              {PRESET_ROLES.map(preset => (
                <button
                  key={preset.role}
                  onClick={() => quickAdd(preset)}
                  className="bg-blue-100 hover:bg-blue-300 text-blue-800 px-5 py-3 rounded-2xl text-xl font-semibold flex items-center gap-2 border border-blue-300 shadow-md transition-all duration-200 ease-in-out transform hover:scale-105 hover:shadow-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-opacity-50"
                >
                  <span className="text-3xl">{preset.icon}</span>{preset.role}
                </button>
              ))}
            </div>
            <div className="flex flex-col md:flex-row gap-6 items-center justify-center mb-4">
              <input
                className="border-2 border-blue-300 p-4 text-2xl rounded-2xl w-full md:w-80 focus:outline-none focus:border-blue-500 transition-all shadow-md bg-white"
                placeholder="ÂßìÂêç"
                value={name}
                onChange={e => setName(e.target.value)}
              />
              <input
                className="border-2 border-blue-300 p-4 text-2xl rounded-2xl w-full md:w-80 focus:outline-none focus:border-blue-500 transition-all shadow-md bg-white"
                placeholder="ÂÖ≥Á≥ª (Â¶Ç: ÊúãÂèã, ÂÆ†Áâ©)"
                value={role}
                onChange={e => setRole(e.target.value)}
              />
              <select
                className="border-2 border-blue-300 p-4 text-3xl rounded-2xl bg-white shadow-md focus:outline-none focus:border-blue-500 transition-all cursor-pointer"
                value={icon}
                onChange={e => setIcon(e.target.value)}
              >
                {PRESET_ROLES.map(preset => (
                  <option key={preset.role} value={preset.icon}>
                    {preset.icon} {preset.role}
                  </option>
                ))}
              </select>
              <button
                onClick={addMember}
                className="bg-gradient-to-r from-blue-600 to-indigo-700 hover:from-blue-700 hover:to-indigo-800 text-white px-10 py-4 rounded-2xl text-2xl font-bold shadow-lg transition-all duration-200 ease-in-out transform hover:scale-105 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-opacity-50"
              >
                ‚ûï Ê∑ªÂä†ÊàêÂëò
              </button>
            </div>
          </div>

          {/* ÁîªÂ∏ÉÂå∫Âüü */}
          <div className="flex justify-center mb-8">
            <Canvas members={members} updateMemberPosition={updateMemberPosition} />
          </div>

          {/* Êìç‰ΩúÊåâÈíÆ */}
          <div className="flex flex-col sm:flex-row justify-center gap-6 mt-6">
            <button
              onClick={analyze}
              disabled={loading || members.length === 0}
              className={`px-10 py-4 text-2xl font-bold rounded-2xl shadow-lg transition-all duration-300 ease-in-out transform ${
                loading || members.length === 0
                  ? 'bg-gray-300 text-gray-600 cursor-not-allowed'
                  : 'bg-gradient-to-r from-green-500 to-teal-600 hover:from-green-600 hover:to-teal-700 text-white hover:scale-105 focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-opacity-50'
              }`}
            >
              {loading ? 'ÂàÜÊûê‰∏≠...' : 'üîç ÂºÄÂßãÂàÜÊûê'}
            </button>
            <button
              onClick={clearMembers}
              disabled={members.length === 0}
              className="bg-white border-2 border-gray-300 text-gray-700 px-8 py-3 rounded-2xl text-xl font-semibold shadow-md hover:bg-gray-100 transition-all duration-200 ease-in-out transform hover:scale-105 disabled:opacity-50 disabled:cursor-not-allowed focus:outline-none focus:ring-2 focus:ring-gray-400 focus:ring-opacity-50"
            >
              üßπ Ê∏ÖÁ©∫ÊâÄÊúâÊàêÂëò
            </button>
          </div>

          {/* ÂàÜÊûêÁªìÊûúÂ±ïÁ§∫ */}
          {analysis && (
            <div className="mt-12 p-8 bg-blue-50 border border-blue-200 rounded-3xl shadow-inner-lg">
              <h3 className="text-3xl font-extrabold text-blue-700 mb-5">üß† ÂàÜÊûêÁªìÊûúÔºö</h3>
              <p className="text-gray-800 text-xl leading-relaxed whitespace-pre-line">
                {analysis}
              </p>
            </div>
          )}
        </div>
      </div>
    </DndProvider>
  );
}
