import React, { useState, useEffect, useRef } from 'react';
import Canvas3D from './Canvas3D';
import axios from 'axios';
import { saveAs } from 'file-saver';
const PRESET_ROLES = [
  { role: '自己', icon: '🧑' },
  { role: '父亲', icon: '👨' },
  { role: '母亲', icon: '👩' },
  { role: '丈夫', icon: '👨' },
  { role: '妻子', icon: '👩' },
  { role: '儿子', icon: '👦' },
  { role: '女儿', icon: '👧' },
  { role: '孩子', icon: '🧒' },
  { role: '祖父', icon: '👴' },
  { role: '祖母', icon: '👵' },
  { role: '配偶', icon: '💑' },
  { role: '哥哥', icon: '🧑‍🦱' },
  { role: '姐姐', icon: '👩‍🦰' },
  { role: '弟弟', icon: '🧑‍🎓' },
  { role: '妹妹', icon: '👧' },
  { role: '继父', icon: '🧔' },
  { role: '继母', icon: '👩‍🦳' },
  { role: '养父', icon: '🧓' },
  { role: '养母', icon: '👵' },
  { role: '朋友', icon: '🤝' },
  { role: '宠物', icon: '🐶' },
];

export default function App() {
  const [members, setMembers] = useState(() => {
    const saved = localStorage.getItem('members');
    return saved ? JSON.parse(saved) : [];
  });
  const [undoStack, setUndoStack] = useState([]);
  const [redoStack, setRedoStack] = useState([]);
  const [name, setName] = useState('');
  const [role, setRole] = useState('');
  const [icon, setIcon] = useState('🧑');
  const [isDeceased, setIsDeceased] = useState(false);
  const [analysis, setAnalysis] = useState('');
  const [loading, setLoading] = useState(false);
  const [dragCompleted, setDragCompleted] = useState(false);
  const [selectedMember, setSelectedMember] = useState(null);
  const [gender, setGender] = useState('');
  const [color, setColor] = useState('#60a5fa');

  useEffect(() => {
    localStorage.setItem('members', JSON.stringify(members));
  }, [members]);

  const pushToUndo = () => setUndoStack([...undoStack, JSON.stringify(members)]);
  const undo = () => {
    if (!undoStack.length) return;
    setRedoStack([...redoStack, JSON.stringify(members)]);
    const prev = JSON.parse(undoStack.pop());
    setUndoStack([...undoStack]);
    setMembers(prev);
  };
  const redo = () => {
    if (!redoStack.length) return;
    setUndoStack([...undoStack, JSON.stringify(members)]);
    const next = JSON.parse(redoStack.pop());
    setRedoStack([...redoStack]);
    setMembers(next);
  };

  const addMember = () => {
    if (!name || !role || !gender) return;
    pushToUndo();
    const x = Math.round((Math.random() - 0.5) * 16);
    const z = Math.round((Math.random() - 0.5) * 16);
    setMembers([
      ...members,
      {
        id: Date.now(), name, role, icon, isDeceased, gender, x, z,
        size: 1, rotation: 0, color
      }
    ]);
    setName(''); setRole(''); setIcon('🧑'); setIsDeceased(false);
    setGender(''); setColor('#60a5fa');
  };

  const quickAdd = preset => {
    setRole(preset.role); setIcon(preset.icon); setName('');
  };

  const onUpdateMember = (id, changes) => {
    pushToUndo();
    setMembers(members => members.map(m => m.id === id ? { ...m, ...changes } : m));
  };

  const generateDescription = () => {
    return members.map(m => {
      const status = m.isDeceased ? '（已故）' : '';
      return `${m.name}（${m.role}）${status}位于3D坐标(${m.x.toFixed(1)}, ${m.z.toFixed(1)})，大小${m.size || 1}`;
    }).join('，');
  };

  const analyze = async () => {
    setLoading(true);
    setAnalysis('');
    try {
      const res = await axios.post('http://127.0.0.1:5000/analyze', {
        description: generateDescription()
      });
      setAnalysis(res.data.analysis);
    } catch {
      setAnalysis('⚠️ 分析失败，请稍后重试。');
    }
    setLoading(false);
  };

  const clearMembers = () => {
    pushToUndo();
    setMembers([]);
    setAnalysis('');
    setDragCompleted(false);
    setSelectedMember(null);
  };

  const handleDragComplete = async () => {
    setDragCompleted(true);
    setLoading(true);
    setAnalysis('');
    try {
      const res = await axios.post('http://127.0.0.1:5000/analyze', {
        description: generateDescription()
      });
      setAnalysis(res.data.analysis);
    } catch {
      setAnalysis('⚠️ 分析失败，请稍后重试。');
    }
    setLoading(false);
  };

  const exportData = () => {
    const blob = new Blob([JSON.stringify(members)], { type: 'application/json' });
    saveAs(blob, 'family-layout.json');
  };

  const importData = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();
    reader.onload = () => {
      try {
        const data = JSON.parse(reader.result);
        if (Array.isArray(data)) setMembers(data);
      } catch {
        alert('导入失败，文件格式错误');
      }
    };
    reader.readAsText(file);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-blue-100 py-6 px-4">
      <div className="max-w-7xl mx-auto bg-white rounded-3xl shadow-2xl p-6 sm:p-10">
        <h1 className="text-4xl font-bold text-blue-700 text-center mb-6 drop-shadow-sm">3D家庭排列分析</h1>

        {/* 快捷添加区域 */}
        <div className="bg-blue-50 border border-blue-200 rounded-3xl p-4 mb-6 flex flex-wrap gap-2 justify-center">
          {PRESET_ROLES.map(p => (
            <button
              key={p.role}
              onClick={() => quickAdd(p)}
              className="bg-blue-100 hover:bg-blue-300 text-blue-800 px-4 py-2 rounded-2xl text-xl font-bold"
            >
              <span className="text-2xl mr-1">{p.icon}</span>{p.role}
            </button>
          ))}
        </div>

        {/* 添加成员表单 */}
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4 mb-4">
          <input className="p-4 rounded-xl border" placeholder="姓名" value={name} onChange={e => setName(e.target.value)} />
          <input className="p-4 rounded-xl border" placeholder="关系" value={role} onChange={e => setRole(e.target.value)} />
          <select className="p-4 rounded-xl border" value={icon} onChange={e => setIcon(e.target.value)}>
            {PRESET_ROLES.map(p => <option key={p.role} value={p.icon}>{p.icon}</option>)}
          </select>
          <select className="p-4 rounded-xl border" value={gender} onChange={e => setGender(e.target.value)}>
            <option value="">性别</option>
            <option value="male">男</option>
            <option value="female">女</option>
          </select>
          <input className="p-4 rounded-xl border" type="color" value={color} onChange={e => setColor(e.target.value)} title="自定义颜色" />
          <label className="flex items-center space-x-2">
            <input type="checkbox" checked={isDeceased} onChange={e => setIsDeceased(e.target.checked)} />
            <span>已故</span>
          </label>
          <button onClick={addMember} className="bg-blue-600 text-white font-bold rounded-xl p-4 hover:bg-blue-700">添加成员</button>
        </div>

        {/* 操作按钮 */}
        <div className="flex flex-wrap gap-3 justify-center mb-4">
          <button onClick={undo} className="px-4 py-2 bg-gray-200 rounded-xl">↩️ 撤销</button>
          <button onClick={redo} className="px-4 py-2 bg-gray-200 rounded-xl">↪️ 重做</button>
          <button onClick={handleDragComplete} className="px-4 py-2 bg-purple-500 text-white rounded-xl">🧠 3D分析</button>
          <button onClick={analyze} className="px-4 py-2 bg-green-500 text-white rounded-xl">🔍 文字分析</button>
          <button onClick={clearMembers} className="px-4 py-2 bg-red-100 text-red-700 rounded-xl">🧹 清空</button>
          <button onClick={exportData} className="px-4 py-2 bg-blue-100 rounded-xl">⬇️ 导出布局</button>
          <label className="px-4 py-2 bg-blue-100 rounded-xl cursor-pointer">
            ⬆️ 导入布局
            <input type="file" accept=".json" onChange={importData} className="hidden" />
          </label>
        </div>

        <Canvas3D 
          members={members} 
          onUpdateMember={onUpdateMember}
          selectedMember={selectedMember}
          onSelectMember={setSelectedMember}
        />

        {analysis && (
          <div className="mt-6 p-6 bg-blue-50 border rounded-xl text-gray-800">
            <div className="font-bold text-blue-700 mb-2">🧠 分析结果：</div>
            <pre className="whitespace-pre-line">{analysis}</pre>
          </div>
        )}
      </div>
    </div>
  );
}
