import React, { useState, useEffect } from 'react';
import { useDrag } from 'react-dnd';

const directionOrder = ['up', 'right', 'down', 'left'];
const directionAngle = { up: 0, right: 90, down: 180, left: 270 };

const CANVAS_WIDTH = 1200;
const CANVAS_HEIGHT = 500;

export default function FamilyMember({ member, onUpdate }) {
  const [{ isDragging }, drag] = useDrag({
    type: 'MEMBER',
    item: { id: member.id },
    collect: monitor => ({
      isDragging: monitor.isDragging(),
    }),
  });

  const [hovered, setHovered] = useState(false);
  const [resizing, setResizing] = useState(false);
  const [resizeStart, setResizeStart] = useState(null);

  // 缩放时保持中心点不变
  useEffect(() => {
    if (!resizing) return;
    const handleMove = e => {
      const dx = e.clientX - resizeStart.x;
      const dy = e.clientY - resizeStart.y;
      const delta = Math.max(Math.abs(dx), Math.abs(dy)) * (dx < 0 || dy < 0 ? -1 : 1);
      const base = Math.max(resizeStart.width, resizeStart.height);
      const newSize = Math.max(40, base + delta);

      // 保持中心点不变
      const centerX = member.x + resizeStart.width / 2;
      const centerY = member.y + resizeStart.height / 2;
      let newX = centerX - newSize / 2;
      let newY = centerY - newSize / 2;

      // 限制不超出画布
      newX = Math.max(0, Math.min(newX, CANVAS_WIDTH - newSize));
      newY = Math.max(0, Math.min(newY, CANVAS_HEIGHT - newSize));

      onUpdate && onUpdate(member.id, { width: newSize, height: newSize, x: newX, y: newY });
    };
    const handleUp = () => setResizing(false);
    window.addEventListener('mousemove', handleMove);
    window.addEventListener('mouseup', handleUp);
    return () => {
      window.removeEventListener('mousemove', handleMove);
      window.removeEventListener('mouseup', handleUp);
    };
  }, [resizing, resizeStart, onUpdate, member.x, member.y]);

  // 旋转按钮
  const handleRotate = e => {
    e.stopPropagation();
    const idx = directionOrder.indexOf(member.direction || 'up');
    const next = directionOrder[(idx + 1) % 4];
    onUpdate && onUpdate(member.id, { direction: next });
  };

  // 缩放手柄
  const handleResizeMouseDown = e => {
    e.stopPropagation();
    setResizing(true);
    setResizeStart({ x: e.clientX, y: e.clientY, width: member.width, height: member.height });
    document.body.style.setProperty('cursor', 'nwse-resize', 'important');
  };

  // 渲染
  const width = member.width || 72;
  const height = member.height || 72;

  return (
    <div
      ref={drag}
      style={{
        position: 'absolute',
        left: member.x,
        top: member.y,
        width,
        height,
        background: 'white',
        border: '2px solid #a5b4fc',
        borderRadius: 12,
        boxShadow: hovered ? '0 8px 24px rgba(0,0,0,0.2)' : '0 6px 20px rgba(0,0,0,0.15)',
        zIndex: isDragging ? 2000 : 1000,
        userSelect: 'none',
        transition: 'box-shadow 0.2s',
      }}
      onMouseEnter={() => setHovered(true)}
      onMouseLeave={() => setHovered(false)}
    >
      {/* 朝向三角形 */}
      <svg
        width="22"
        height="22"
        style={{
          position: 'absolute',
          top: -22,
          left: '50%',
          zIndex: 20,
          transform: `translateX(-50%) rotate(${directionAngle[member.direction || 'up']}deg)`
        }}
      >
        <polygon points="11,0 22,22 0,22" fill="#6366f1" />
      </svg>
      {/* 旋转按钮 */}
      <button
        onClick={handleRotate}
        style={{
          position: 'absolute',
          top: -30,
          right: 0,
          zIndex: 20,
          background: 'rgba(99,102,241,0.12)',
          border: 'none',
          borderRadius: '50%',
          width: 28,
          height: 28,
          cursor: 'pointer',
          display: hovered ? 'block' : 'none',
          padding: 3,
        }}
        title="旋转朝向"
      >
        <svg width="16" height="16" viewBox="0 0 16 16">
          <path d="M8 2v2.5a.5.5 0 0 0 .5.5H11" stroke="#6366f1" strokeWidth="1.5" fill="none" />
          <path d="M13.5 8A5.5 5.5 0 1 1 8 2" stroke="#6366f1" strokeWidth="1.5" fill="none" />
        </svg>
      </button>
      {/* 缩放手柄 */}
      <div
        onMouseDown={handleResizeMouseDown}
        style={{
          position: 'absolute',
          right: -8,
          bottom: -8,
          width: 18,
          height: 18,
          background: '#fff',
          border: '2px solid #6366f1',
          borderRadius: 6,
          cursor: 'nwse-resize',
          zIndex: 30,
          boxShadow: '0 1px 4px rgba(0,0,0,0.08)',
          display: hovered ? 'block' : 'none',
        }}
        title="缩放"
      >
        <svg width="14" height="14" style={{ margin: 2 }}>
          <polyline points="2,12 12,12 12,2" fill="none" stroke="#6366f1" strokeWidth="2" />
        </svg>
      </div>
      {/* 其它内容... */}
      <div style={{ textAlign: 'center', marginTop: 8 }}>{member.name}</div>
      <div style={{ textAlign: 'center', color: '#3b82f6' }}>{member.role}</div>
    </div>
  );
}
