import React, { useRef } from 'react';
import { useDrop } from 'react-dnd';
import FamilyMember from './FamilyMember';
import './index.css';

const MEMBER_SIZE = 90;
const MIN_DISTANCE = 0; // 成员之间最小间距

/**
 * 获取避免重叠的位置
 */
function getNonOverlappingPosition(x, y, members, currentId, canvasWidth, canvasHeight) {
  let safeX = x;
  let safeY = y;
  let tries = 0;

  while (tries < 30) {
    const isOverlapping = members.some(
      m =>
        m.id !== currentId &&
        Math.abs(m.x - safeX) < MEMBER_SIZE - MIN_DISTANCE &&
        Math.abs(m.y - safeY) < MEMBER_SIZE - MIN_DISTANCE
    );

    if (!isOverlapping) break;

    safeX = Math.max(0, Math.min(safeX + 15, canvasWidth - MEMBER_SIZE));
    safeY = Math.max(0, Math.min(safeY + 15, canvasHeight - MEMBER_SIZE));
    tries++;
  }

  return { x: safeX, y: safeY };
}

const Canvas = ({ members, updateMemberPosition }) => {
  const dragOffsetRef = useRef({});

  const [, drop] = useDrop({
    accept: 'MEMBER',

    hover: (item, monitor) => {
      if (!dragOffsetRef.current[item.id] && monitor.isOver({ shallow: true })) {
        const initialOffset = monitor.getInitialClientOffset();
        const sourceOffset = monitor.getInitialSourceClientOffset();

        if (initialOffset && sourceOffset) {
          dragOffsetRef.current[item.id] = {
            offsetX: initialOffset.x - sourceOffset.x,
            offsetY: initialOffset.y - sourceOffset.y,
          };
        }
      }
    },

    drop: (item, monitor) => {
      const clientOffset = monitor.getClientOffset();
      const canvas = document.getElementById('canvas-area');
      const canvasRect = canvas?.getBoundingClientRect();

      if (!clientOffset || !canvasRect) return;

      // 偏移修正，防止图标中心飘到边界外
      let offsetX = MEMBER_SIZE / 2;
      let offsetY = MEMBER_SIZE / 2;

      if (dragOffsetRef.current[item.id]) {
        offsetX = dragOffsetRef.current[item.id].offsetX;
        offsetY = dragOffsetRef.current[item.id].offsetY;
      }

      // 原始拖动目标坐标
      let rawX = clientOffset.x - canvasRect.left - offsetX;
      let rawY = clientOffset.y - canvasRect.top - offsetY;

      // 确保坐标在边界内（人物整体都在画布内）
      const clampedX = Math.max(0, Math.min(rawX, canvasRect.width - MEMBER_SIZE));
      const clampedY = Math.max(0, Math.min(rawY, canvasRect.height - MEMBER_SIZE));

      const { x: safeX, y: safeY } = getNonOverlappingPosition(
        clampedX, clampedY, members, item.id, canvasRect.width, canvasRect.height
      );

      updateMemberPosition(item.id, safeX, safeY);
      delete dragOffsetRef.current[item.id];
    }
  });

  return (
    <div
      id="canvas-area"
      ref={drop}
      className="mx-auto relative bg-gray-100 border-4 border-black rounded-xl overflow-hidden shadow-inner select-none"
      style={{
        width: '100%',
        maxWidth: 900,
        minWidth: 600,
        height: 320,
        minHeight: 200,
        backgroundImage: `
          radial-gradient(circle, #e0f2fe 1px, transparent 1px),
          linear-gradient(#f3f4f6, #f3f4f6)
        `,
        backgroundSize: '20px 20px, 100% 100%',
        backgroundPosition: '0 0, 0 0',
      }}
    >
      {members.length === 0 ? (
        <div className="absolute left-1/2 top-1/2 -translate-x-1/2 -translate-y-1/2 text-blue-400 text-xl font-semibold select-none">
          请添加家庭成员并拖动到画布
        </div>
      ) : (
        members.map(member => (
          <FamilyMember key={member.id} member={member} />
        ))
      )}
    </div>
  );
};

export default Canvas;
