import React, { useState, useEffect, useCallback } from 'react';
import { useDrag } from 'react-dnd';

const directionOrder = ['north', 'northeast', 'east', 'southeast', 'south', 'southwest', 'west', 'northwest'];
const directionAngle = { 
  north: 0, 
  northeast: 45, 
  east: 90, 
  southeast: 135, 
  south: 180, 
  southwest: 225, 
  west: 270, 
  northwest: 315 
};

// 箭头位置配置（相对于框体的位置）
const arrowPositions = {
  north: { top: -22, left: '50%', transform: 'translateX(-50%)' },
  northeast: { top: -16, right: -16 },
  east: { top: '50%', right: -22, transform: 'translateY(-50%)' },
  southeast: { bottom: -16, right: -16 },
  south: { bottom: -22, left: '50%', transform: 'translateX(-50%)' },
  southwest: { bottom: -16, left: -16 },
  west: { top: '50%', left: -22, transform: 'translateY(-50%)' },
  northwest: { top: -16, left: -16 }
};

const FamilyMember = ({ member, onUpdate }) => {
  const [{ isDragging }, drag] = useDrag({
    type: 'MEMBER',
    item: { id: member.id },
    collect: monitor => ({
      isDragging: monitor.isDragging(),
    }),
  });

  // 确保member有width和height属性，否则使用默认值
  const width = member.width || 72;
  const height = member.height || 72;

  const [hovered, setHovered] = useState(false);
  const [resizing, setResizing] = useState(false);
  const [resizeStart, setResizeStart] = useState(null);

  const handleRotate = useCallback((e) => {
    e.preventDefault();
    e.stopPropagation(); // 阻止事件冒泡，避免触发拖拽
    // 使用validDirection确保当前值有效
    const idx = directionOrder.indexOf(validDirection);
    const next = directionOrder[(idx + 1) % directionOrder.length];
    console.log('箭头朝向:', validDirection, '->', next); // 调试日志
    if (onUpdate) {
      onUpdate(member.id, { direction: next });
    }
  }, [validDirection, member.id, onUpdate]);

  const handleResizeMouseDown = useCallback((e) => {
    e.preventDefault();
    e.stopPropagation(); // 阻止事件冒泡
    console.log('开始缩放'); // 调试日志
    setResizing(true);
    setResizeStart({ x: e.clientX, y: e.clientY, width, height });
    document.body.style.setProperty('cursor', 'nwse-resize', 'important');
  }, [width, height]);

  useEffect(() => {
    if (!resizing || !resizeStart) return;

    const handleMove = (e) => {
      const dx = e.clientX - resizeStart.x;
      const dy = e.clientY - resizeStart.y;
      // 取最大绝对值，保持等比例缩放
      const delta = Math.max(Math.abs(dx), Math.abs(dy)) * (dx < 0 || dy < 0 ? -1 : 1);
      const base = Math.max(resizeStart.width, resizeStart.height);
      const newSize = Math.max(40, base + delta); // 最小尺寸为40

      onUpdate && onUpdate(member.id, { width: newSize, height: newSize });
    };

    const handleUp = () => {
      setResizing(false);
      setResizeStart(null);
      document.body.style.cursor = '';
    };

    document.addEventListener('mousemove', handleMove);
    document.addEventListener('mouseup', handleUp);

    return () => {
      document.removeEventListener('mousemove', handleMove);
      document.removeEventListener('mouseup', handleUp);
    };
  }, [resizing, resizeStart, onUpdate, member.id]);

  // 确保direction值有效，如果无效则使用默认值
  const validDirection = member.direction && arrowPositions[member.direction] ? member.direction : 'north';

  const renderDirectionTriangle = useCallback(() => {
    const position = arrowPositions[validDirection];
    const angle = directionAngle[validDirection] || 0;

    // 组合transform属性
    const baseTransform = position.transform || '';
    const rotateTransform = `rotate(${angle}deg)`;
    const combinedTransform = baseTransform ? `${baseTransform} ${rotateTransform}` : rotateTransform;

    return (
      <svg
        width="22"
        height="22"
        style={{
          position: 'absolute',
          top: position.top,
          left: position.left,
          right: position.right,
          bottom: position.bottom,
          zIndex: 20,
          transform: combinedTransform
        }}
      >
        <polygon points="11,0 22,22 0,22" fill="#6366f1" />
      </svg>
    );
  }, [validDirection]);

  const renderResizeHandle = useCallback(() => (
    <div
      onMouseDown={handleResizeMouseDown}
      style={{
        position: 'absolute',
        right: -25,
        bottom: -25,
        width: 24,
        height: 24,
        background: '#fff',
        border: '2px solid #6366f1',
        borderRadius: 8,
        cursor: 'nwse-resize',
        zIndex: 30,
        boxShadow: '0 2px 8px rgba(0,0,0,0.15)',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center'
      }}
      title="缩放"
    >
      <svg width="16" height="16">
        <polyline points="3,13 13,13 13,3" fill="none" stroke="#6366f1" strokeWidth="2" />
        <polyline points="9,7 13,3 9,7" fill="none" stroke="#6366f1" strokeWidth="2" />
        <polyline points="7,9 13,13 7,9" fill="none" stroke="#6366f1" strokeWidth="2" />
      </svg>
    </div>
  ), [handleResizeMouseDown]);

  // 根据shape属性决定容器样式
  const getContainerStyle = () => {
    let baseStyle = {
      width,
      height,
      minWidth: 40,
      minHeight: 40,
      cursor: isDragging ? 'grabbing' : 'grab',
      opacity: isDragging ? 0.8 : 1,
      background: 'rgba(255,255,255,0.98)',
      border: '3px solid #a5b4fc',
      boxShadow: hovered ? '0 8px 24px rgba(0,0,0,0.2)' : '0 6px 20px rgba(0,0,0,0.15)',
      transition: 'box-shadow 0.2s ease, transform 0.2s ease',
      transform: isDragging ? 'scale(1.02)' : 'scale(1)',
      willChange: 'transform',
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center',
      userSelect: 'none',
      position: 'relative',
    };

    // 根据形状设置边框圆角
    switch (member.shape) {
      case 'circle':
        baseStyle.borderRadius = '50%';
        break;
      case 'triangle':
        baseStyle.borderRadius = '0';
        baseStyle.clipPath = 'polygon(50% 0%, 0% 100%, 100% 100%)';
        break;
      case 'square':
      default:
        baseStyle.borderRadius = '8px';
        break;
    }

    return baseStyle;
  };

  return (
    <div
      style={{ position: 'absolute', left: member.x, top: member.y, zIndex: 2000 }}
      onMouseEnter={() => setHovered(true)}
      onMouseLeave={() => setHovered(false)}
    >
      {/* 旋转按钮 - 固定在左上角 */}
      <button
        onClick={handleRotate}
        onMouseDown={(e) => e.stopPropagation()}
        style={{
          position: 'absolute',
          top: -40,
          left: -40,
          zIndex: 20,
          background: '#fff',
          border: '2px solid #6366f1',
          borderRadius: '50%',
          width: 32,
          height: 32,
          cursor: 'pointer',
          padding: 0,
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          boxShadow: '0 2px 8px rgba(0,0,0,0.15)'
        }}
        title={`箭头朝向: ${validDirection}`}
      >
        <svg width="18" height="18" viewBox="0 0 16 16">
          <path d="M8 2v2.5a.5.5 0 0 0 .5.5H11" stroke="#6366f1" strokeWidth="1.5" fill="none" />
          <path d="M13.5 8A5.5 5.5 0 1 1 8 2" stroke="#6366f1" strokeWidth="1.5" fill="none" />
        </svg>
      </button>

      {/* 成员框本体 */}
      <div
        ref={drag}
        style={getContainerStyle()}
      >
        {renderDirectionTriangle()}

        {/* 死亡叉号 */}
        {member.isDeceased && (
          <div
            style={{
              position: 'absolute',
              top: '50%',
              left: '50%',
              transform: 'translate(-50%, -50%)',
              fontSize: Math.min(width * 2, 60),
              color: '#6b7280',
              fontWeight: 'bold',
              zIndex: 2001,
              textShadow: '2px 2px 4px rgba(0,0,0,0.3)',
              opacity: 0.6,
              pointerEvents: 'none',
            }}
          >
            ✕
          </div>
        )}

        <div
          style={{
            fontSize: Math.max(12, Math.min(width * 0.38, 28)),
            marginBottom: 2,
            transition: 'transform 0.2s',
          }}
        >
          {member.icon || '🧑'}
        </div>
        <div
          style={{
            fontWeight: 'bold',
            color: '#1f2937',
            fontSize: Math.max(10, width / 6.5),
            marginBottom: 1,
          }}
        >
          {member.name}
        </div>
        <div
          style={{
            fontSize: Math.max(8, width / 8),
            color: '#3b82f6',
            fontWeight: 600,
            letterSpacing: 0.5,
          }}
        >
          {member.role}
        </div>

        {renderResizeHandle()}
      </div>
    </div>
  );
};

export default FamilyMember;
